css:
@-moz-document domain("ebay-kleinanzeigen.de") {
body {
    background: url("https://wallpaperaccess.com/full/143729.jpg") #000;
    /* Change link to personalize bg-img-url. */
    background-repeat: no-repeat;
    background-position: center;
    background-attachment: fixed;
    color: #dedede;
}

a {
    color: #00cc66
}
a:hover {
    color: #00b337;
    text-decoration: underline;
}

fieldset {
    background: none;
    background: url("https://echstreme.de/gfx/chstrm/trans-50.png");
}

.simpletag {
    background: #000;
    color: #d55;
    border: 1px solid #fff;
}

.saved-searches-badge {
    background: #fd0;
    color: #f00;
    font-weight: bold;
    margin-top: -10px !important;
    margin-left: 20px !important;
}


.featuretable {
    color: #dedede !important;
}

.bubblebox {
    color: #000;
    background: #5a9
}

#site-header {
    background: url("https://echstreme.de/gfx/chstrm/trans-80.png");
    margin-bottom: 0px;
}

#site-content {
    background: url("https://echstreme.de/gfx/chstrm/trans-80.png");
    border: 1px solid #000;
    border-bottom-left-radius: 25px;
    border-bottom-right-radius: 25px;
    border-top: 0px;
}

#root #site-content,
#root #site-content .l-container,
#root #site-content .l-container .contentbox-unpadded {
    border: none !important;
    background: none;
}

#site-footer {
    background: none;
    background: url("https://echstreme.de/gfx/chstrm/trans-80.png");
    font-size: 8pt;
}

icon,
icon-link-icon,
.icon-eye-black {
    background-color: #88f !important;
    background-blend-mode: luminosity;
}

icon,
icon-link-icon,
.icon-star-open-black {
    background-color: #fe0 !important;
    background-blend-mode: multiply;
}

/* MAIN PAGE */
/* #home-cntnt {} */
.l-homepage-navigation-col {
    background: none;
}
#home-ctgrs {
    background: none;
    background: url("https://echstreme.de/gfx/chstrm/trans-50.png");
}

.tilesbox-featured {
    background: none;
    border: 3px dotted #f5af02;
    border-radius: 10px;
}

.tilesbox--header {
    margin-top: -10px;
}

.itemtile-condensed,
.itemtile-fullpic,
.imagebox {
    background: none;
    background: url("https://echstreme.de/gfx/chstrm/trans-50.png");
    border: 1px solid #000;
    border-radius: 5px;
}
.itemtile-title > a {
    color: #77d499 !important;
}

.is-nopic {
    filter: invert();
}
.galleryimage-large--cover {
    background-color: #000;
    filter: blur(1);
    background-blend-mode: hard-light;
    opacity: 0.8;
    background-position: center;
    background-attachment: fixed;
}

/* MSG BOX (Nachrichten) */
.listitem-container {
    background: none;
    background: url("https://echstreme.de/gfx/chstrm/trans-50.png");
}

.conversationitem,
.is-active,
.conversationitem.isactive {
    background: none !important;
}

.msgbox-from {
    background: #0008;
    border: 1px solid #deda;
}

/* WERBUNG! */
#store-gallery {
    /* WERBUNG!? */
    background: #f00 !important;
    zdisplay: none;
}

#banner-skyscraper,
.banner-supersize,
#my-atf,
#page-pagination {
    display: none;
}

/* ARTIKELSEITE */
.contentbox,
.splitlinebox,
.sectionbox,
.cardbox,
footer {
    background: none !important;
    background: url("https://echstreme.de/gfx/chstrm/trans-50.png") !important;
    border: 1px solid #000;
    border-radius: 5px;
}

.cardbox {
    margin-bottom: 5px;
}

#liberty-vip_pro-middle {
    display: none;
}

.contentbox-unpadded {
    background: none;
    background: url("https://echstreme.de/gfx/chstrm/trans-50.png");
}

/* FAV-LIST ITEMS */
.ad-listitem {
    border: 2px dashed #dedede;
    border-bottom-left-radius: 25px;
    border-bottom-right-radius: 25px;
    margin-bottom: 5px;
}

.ad-deleted {
    color: #adadad;
    background: #222;
    text-decoration: line-through;
}

.ad-listitem-cover {
    background: none;
}

.badge-unavailable {
    background: #c00;
}

.aditem-footer {
    background: none;
    background: url("https://echstreme.de/gfx/chstrm/trans-80.png") !important;
}



/* MY ADS */
.userprofile,
.ownprofile-main {
    margin: 0px !important;
    margin-top: 1px !important;
}

.actionlist-body {
    background: none;
    background: url("https://echstreme.de/gfx/chstrm/trans-50.png");
}
}
