css:
@-moz-document domain("notion.so")
{
/*
* Name:			Notion.so - Scrollbar
* Homepage:	https://userstyles.org/users/603130
* Author:		Steve yang
* Version:		v1.0
* Updated: 	  02.14.2020
*/



/** Left vertical scrollbar **/
  .notion-sidebar .notion-scroller.vertical::-webkit-scrollbar {
  	width: 0px;
  }
  .notion-sidebar .notion-scroller.vertical::-webkit-scrollbar-track {
  	background: rgb(66, 73, 78)
  }
  .notion-sidebar .notion-scroller.vertical::-webkit-scrollbar-thumb {
  	background: rgb(117, 117, 117);
  	border-radius: 4px;
  }
  .notion-sidebar:hover > .notion-scroller.vertical::-webkit-scrollbar {
  	width: 3px;
  }

/** Right vertical scrollbar **/
  .notion-frame .notion-scroller.vertical::-webkit-scrollbar {
  	color: transparent;
  	background: transparent;
  	width: 0px;
  	/** Hidden Right vertical scrollbar **/
  }

  .notion-frame .notion-scroller.vertical::-webkit-scrollbar-track {
  	background: rgb(66, 73, 78)
  }
  .notion-frame .notion-scroller.vertical::-webkit-scrollbar-thumb {
  	background: transparent;
  }
  .notion-frame:hover > .notion-scroller.vertical::-webkit-scrollbar-thumb {
  	background: rgb(117, 117, 117);
  	border-radius: 4px;
  }

  /** 
::-webkit-scrollbar-corner{
        background-color: rgba(255, 255, 255, 0);
    } **/

  /** horizontal scrollbar **/
  .notion-selectable .notion-scroller.horizontal::-webkit-scrollbar {
  	color: transparent;
  	background: transparent;
  	height: 15px;
  }
  .notion-selectable .notion-scroller.horizontal::-webkit-scrollbar-track {
  	background: rgb(66, 73, 78);
  	margin-left: 270px;
  	margin-right: 189px;
  	border-radius: 20px;
  }
  .notion-selectable .notion-scroller.horizontal::-webkit-scrollbar-thumb {
  	background: transparent;
  	border-radius: 20px;
  }
  .notion-selectable:hover > .notion-scroller.horizontal::-webkit-scrollbar-thumb {
  	background: rgb(117, 117, 117);
	height: 5px;
  }
}
