/** colomun header center 
.notion-table-view .notion-selectable div div div div div div {
	margin: 0px auto;
}*/
/** colomun header cell  */
.notion-table-view-header-cell {
	background: rgba(40, 125, 201, .85);
	font-family: sans-serif;
	font-weight: bold;
	color: rgb(255, 255, 255)
}


.notion-table-view .notion-collection-item div {
	text-align: left !important;
}


/** Change all selection bar of SVG icons w/h  **/
.notion-table-view svg {
	height: 16px !important;
	width: 16px !important;
}

/* Change Table SVG icons w/h 
.notion-table-view .notion-collection-item svg {
        height: 16px !important;
        width: 16px !important;
} */

/** Table line alignment **/
.notion-table-view {
	min-width: 0px !important;
	background-size: 100% 0px !important;
}

/** Remove link, URL unlderline in Table **/
[style^="line-height: 1.5; white-space: normal; word-break: break-word; pointer-events: none; background-image: linear-gradient(to right, rgba(255, 255, 255, 0.14) 0%, rgba(255, 255, 255, 0.14) 100%); background-repeat: repeat-x; background-position: 0px 100%; background-size: 100% 1px; font-weight: 500;"] {
	background-size: 100% 0px !important;
}


[style="line-height: 1.5; white-space: pre-wrap; word-break: break-word; pointer-events: none; background-image: linear-gradient(to right, rgba(255, 255, 255, 0.14) 0%, rgba(255, 255, 255, 0.14) 100%); background-repeat: repeat-x; background-position: 0px 100%; background-size: 100% 1px;"] {
	background-size: 100% 0px !important;
}

/*
.notion-table-view .notion-collection-item div div {
	 margin: 0px auto !important;

}
	*/
/*
/*Left Align 2nd Column - Name Column - CHANGE THIS!*/
/*Change div:nth-child(2) to the different number of column
    Like - div:nth-child(5) - Left align 5th Column.
    */
/*
.notion-table-view .notion-collection-item div:nth-child(1) div:nth-child(2) {
		text-align: left !important;
} */
/* Change all page of table selection 
.notion-table-view div:nth-child(1) > div:nth-child(2) > div:nth-child(1) {
		width: 33px !important;
    	text-align: -webkit-center;
} 


 /** Change data-block-id, div:nth-child(N) from left to right  
/**  3 selectors mapping to 3 column attribute -  Column header. Column cells and calculate cell at the bottom **/
[data-block-id^="e26da6f3-90f4-4a27-9c97-15fb1235b126"] > [style^="display: flex; position: absolute; background: rgb(47, 52, 55); z-index: 82; height: 33px; color: rgba(255, 255, 255, 0.6);"] > div:nth-child(1) > div:nth-child(1) > div:nth-child(1),
[data-block-id^="e26da6f3-90f4-4a27-9c97-15fb1235b126"] > [style^="position: relative; min-width: calc(100% - 192px);"] > [data-block-id] > div:nth-child(1),

[data-block-id^="e26da6f3-90f4-4a27-9c97-15fb1235b126"] > div:nth-child(1) > div:nth-child(2) {
	width: 33px !important;
	text-align: -webkit-center !important;
}


[data-block-id^="e26da6f3-90f4-4a27-9c97-15fb1235b126"] > [style^="display: flex; position: absolute; background: rgb(47, 52, 55); z-index: 82; height: 33px; color: rgba(255, 255, 255, 0.6);"] > div:nth-child(1) > div:nth-child(2) > div:nth-child(1),
[data-block-id^="e26da6f3-90f4-4a27-9c97-15fb1235b126"] > [style^="position: relative; min-width: calc(100% - 192px);"] > [data-block-id] > div:nth-child(2),

[data-block-id^="e26da6f3-90f4-4a27-9c97-15fb1235b126"] > div:nth-child(1) > div:nth-child(2) {
	width: 33px !important;
	text-align: -webkit-center !important;
}



[data-block-id^="21826372-9c6e-465c-bdf7-d8bacba776db"] > [style^="display: flex; position: absolute; background: rgb(47, 52, 55); z-index: 82; height: 33px; color: rgba(255, 255, 255, 0.6);"] > div:nth-child(1) > div:nth-child(2) > div:nth-child(1),
[data-block-id^="21826372-9c6e-465c-bdf7-d8bacba776db"] > [style^="position: relative; min-width: calc(100% - 192px);"] > [data-block-id] > div:nth-child(2),

[data-block-id^="21826372-9c6e-465c-bdf7-d8bacba776db"] > div:nth-child(1) > div:nth-child(2) {
	width: 33px !important;
	text-align: -webkit-center !important;
}


/** Change all SVG icon w/h  
[data-block-id^="e26da6f3-90f4-4a27-9c97-15fb1235b126"] [style^="display: flex; position: absolute; background: rgb(47, 52, 55); z-index: 82; height: 33px; color: rgba(255, 255, 255, 0.6);"] > div:nth-child(1) svg {
	height: 16px !important;
	width: 16px !important;
}**/

/** kanban board
[style^="position: absolute; background: rgba(78, 75, 75, 0); height: 44px; z-index: 82; display: flex; width: 100%; border-top: 1px solid rgba(255, 255, 255, 0.14); padding-top: 4px; transform: translate3d(0px, 0px, 0px);"] {
	    padding-right: 500px !important;
	
}
 **/


/*********************************************/
.notion-table-view .notion-collection-item div div div {
	margin-bottom: 6px !important;
	padding-top: 3px !important;
}

.notion-table-view .notion-collection-item {
	align-items: center;
}


.notion-table-view .notion-collection-item .notion-record-icon {
	margin-right: 5px !important;
	margin-bottom: 1px !important;
}

.notion-table-view .notion-collection-item .notion-record-icon div {
	padding-top: 0px !important;
}

.notion-table-view .checkboxSquare {
	height: 50px !important;
	margin-top: 3px;
	padding-top: 0px !important;
	padding-bottom: 1px !important;
}

/*********** VERSION 2 - FIXES ************/
.notion-table-view .dragHandle {
	margin-top: -6px;
}

.notion-table-view .openAsPageThick {
	margin-right: 2px;
}

.notion-table-view .check {
	margin-top: 3px;
}



