css:
@-moz-document domain("wuxiaworld.co"), domain("readlightnovel.cc") {
body, .book-wrapper, .bg-img-dim, .triangle-bg {
    background-image: none;
    background: #f8f1d9 url("data:image/png;base64,") !important;
}

.read-container, .detail-container {
    border-color: rgba(0, 0, 0, 0);
    background: #f6edd4 url("data:image/png;base64,");
}

.t-header {
    position: absolute;
}

.triangle-bar {
    border: none !important;
}

.section-list .section .chapter-entity {
    letter-spacing: 0; padding-top: 0;
    font-size: 18px !important;
    font-family: Open Sans,Tahoma,sans-serif !important;
    color: black;
    line-height: 1.5;
}

#disqus_thread,
.t-footer {
    display: none;
}

.book-name, .title-name {
    color: #12121a !important;
}

.book-catalog, .score, .book-state, .author {
    color: #363d4d !important;
}

.star-suite .star-group .star.off {
    color: rgb(0 0 0 / 15%);
}

.chapter-wrapper .chapter-list .chapter-item:nth-child(4n), .chapter-wrapper .chapter-list .chapter-item:nth-child(4n-1) {
    background-color: rgba(0, 0, 0, .05);
}
}

@-moz-document regexp("https://www.wuxiaworld.co/.*/.*html"), regexp("https://www.readlightnovel.cc/.*html") {
.book-wrapper {
    display: none;
}
}
