css:
@-moz-document domain("coub.com") {
/*--------------------------------------*/
.coub.coub--normal-card .coub__viewer {
    width: 150%;
}

.coub.coub--normal-card .coub__description {
    width: 150%;
}


.coub .coub__inner {
    position: absolute;
    right: 150px;
    top: -25px;
}

.viewer.viewer--v2.x2 {
    width: auto !important;
    height: auto !important;
    margin: auto !important;
}


.viewer.viewer--v2.x2.-fullscreen {
    width: auto !important;
    height: 100% !important;
    margin: auto !important;
}


/*--------------------------------------*/
.stories-block__container {
    visibility: hidden;
    position: absolute;
}

.channels-block {
    visibility: hidden;
    position: absolute;
}

.timeline-right-block {
    position: absolute;
    visibility: hidden;
    margin-left: -1000px;
}

.-fill.viewer__suggests {
    visibility: hidden;
    position: fixed;
}


.page-menu__view-selector {
    right: 280px;
}

.page-menu__view-selector {
    right: 170px;
}

.page-menu,
.page-menu__inner {
    height: 35px
}

.page-menu__item:not(.list__item) {
    height: 10px
}

.page-menu__inner:not(.-mouse-over) .page-menu__item:not(.list__item).-active::before,
.page-menu__item:not(.list__item).-hovered::before {
    height: 3px;
    bottom: -5px
}

.page-menu__item:not(.list__item) {
    font-size: 20px;
    letter-spacing: -0.6px;
    padding: 0px 20px;
    cursor: pointer;
    position: relative;
    text-align: center;
    line-height: 0px;
    white-space: nowrap;
}


.page-menu.-fixed {
    position: absolute;
    top: 70px;
    z-index: 798;
    width: 1050px;
    border-bottom: 1px solid #e1e1e1;
}

.page-menu {
    border-bottom: 1px solid #010101;
}


.page-menu {
    border-bottom: 0px solid #010101;
}

.header.-scrolled > .header__bg {
    filter: drop-shadow(0 0 0px rgba(0, 050, 125, 0.5)) invert(0%) contrast(125%);
    border-bottom: 0px solid #00000075;
}

.timeline-right-block {
    z-index: 200;
    float: right;
    position: relative;
    width: 210px;
    height: -22px;
    left: 60px;
}

.page-menu,
.page-menu__inner {
    height: 35px;
    width: 950px;
    left: 0px;
    top: 3px;
}

.page-menu__inner:last-of-type {
    margin-left: 130px;
    left: 20px;
}

.page-menu.-fixed {
    position: relative;
    top: 2px;
    z-index: 798;
    width: 755px;
    border-bottom: 1px solid #e1e1e1;
}

.page-menu-dummy {
    visibility: hidden;
    position: fixed;
}

.coub .coub__inner {
    position: relative;
    right: 30px;
}


.header.-scrolled > .header__bg {
    filter: drop-shadow(0 0 0px rgba(0, 050, 125, 0.5)) invert(0%) contrast(125%);
    border-bottom: 0px solid #00000075;
}

.main-menu__inner {
    position: relative;
    background-color: #0000;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, .1);
    height: 1050px;
    width: 250px;
    transition: transform 0.15s ease, visibility 0s linear 0.15s;
    transform: translateX(-100%);
}


.page-menu__inner:last-of-type {
    margin-left: 110px;
    left: 15px;
}


.main-menu.-always-visible:not(.-folded) + .page-container {
    border-left-width: 200px;
}


.header__contents {
    height: 55px;
    width: 100%;
    padding: 1%;
    position: relative;
    margin: 0 auto;
    transition: height .2s ease-in-out, padding-top .2s ease-in-out;
}

.main-menu__fold {
    display: block;
    width: 18px;
    height: 28px;
    background-color: #fff0;
    border: 1px solid #fff0;
    border-left: none;
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
    position: absolute;
    bottom: 500px;
    left: 200px;
    box-sizing: content-box;
    cursor: pointer;
}

.page-menu {
    margin-bottom: 40px;
}

.channel-menu-dropdown__content .list__item.change-channel {
    text-indent: 43px;
    padding-bottom: 25px;
}

.channel-menu-dropdown__content .list__item.logout {
    text-indent: 48px;
    top: -3px;
}

div.page-menu__view-selector {
    position: absolute;
}

.main-menu__nav-item.more-less-toggler {
    padding-left: 23px;
    color: #999999;
    line-height: 40px;
}

.main-menu__fold {
    border-top: none;
}

.main-menu__fold {
    position: relative;
    bottom: 0;
    left: 250px;
}
}
