css:
@-moz-document domain(nvuti.biz) {



.topbar {
	background: #20242d !important;
}

.navbar {
	background: linear-gradient(45deg, #1c65c9 0%, #2c80ff 100%);
}

body {
	background: #1c2028 !important;
}

.card {
	box-shadow: rgba(39, 45, 57, 1) 2px 2px 20px 0;
    background-color: #272d39 !important;
}

.card-token {
    background-image: linear-gradient(-90deg, #272d3f 0%,#272d49 100%);
	color: #fff !important;
}

.input-bordered{
    border: 1px solid #20242d !important;
	color: rgb(255, 255, 255);
	background: rgba(0, 0, 0, 0.1);
}

.nav-tabs-line {
	border-bottom: 2px solid #2c80ff !important;
}

.card-title {
    color: #ffffff !important;
}

body {
    color: #FFFFFF !important;
}

.card-token .card-sub-title {
    color: #FFFFFF !important;
}

.card-sub-title {
	color: #FFFFFF !important;
}

.btn-outline.btn-light {
    color: #FFFFFF !important;
    border-color:#FFFFFF !important;
}

.navbar-menu > li > a {
    color: #FFFFFF !important;
}

a.text-dark:focus, a.text-dark:hover {
    color: #FFFFFF !important;
}

.badge-light.badge-dim {
    cursor: pointer;
    background: linear-gradient(45deg, #1c65c9 0%, #2c80ff 100%);
}

.badge.badge-dim {
    color: #ffffff !important;
}

.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active {
    color: #2c80ff;
    background-color: #272d39 !important;
}

.btn-light:disabled, .btn-light:hover, .btn-light:focus, .btn-light:active, .btn-light.active {
    color: #fff !important;
    background: #216dbd !important;
	border-color:#fff !important;
}

.btn-light {
    color: #fff !important;
    background: #216dbd !important;
	border-color:#216dbd !important;
}

.popup-body {
    background: #272d39 !important;
}

.modal-content {
	background: #272d39 !important;
}

.select2-container--flat .select2-results > .select2-results__options {
    background: #272d39;
}
}
