body, html {
    font-family: 'Source Code Pro', 'Roboto Mono', 'Lucida Console', monospace !important;
    background:#212121;
    margin-top:23px;
}
/* remove ads */
#aside {
    min-height:1px;
}
img[src*="indexa"], img[src*="herramienta_seo"] {
    display:none !important;
}
/*******/

body {
    background:#212121;
}
.title.is-1.has-text-centered {
    visibility:hidden;
}
.title.is-1.has-text-centered:after {
    visibility:visible;
    background-image: url('https://puu.sh/DF6r5/9f822707a4.png');
    background-repeat:no-repeat;
    background-size: 200px auto;
    display:block;
    min-height: 150px;
    content:"";
    margin-left:500px;
    margin-bottom:-73px;
}
.subtitle.has-text-centered {
    margin-left:25px;
    float:left;
    font-size:13pt !important;
    text-transform:lowercase;
    margin-top:20px !important;
    font-weight:bold;
    color:#cacaca;
    
}
.container {
    background-color:#00000000;
    box-shadow:none;
}
#password {
    background-color: #191919;
    border:none;
    font-family: 'Source Code Pro', 'Roboto Mono', 'Lucida Console', monospace;
    color:#cdbae5;
    width:700px;
}
#generate {
    width:680px;
    filter: saturate(.3) sepia(.6) hue-rotate(260deg);
    border-radius: 0;
    margin-top: -12px !important;
    max-height:32px;
    font-size: 10pt;
    padding-bottom:5px !important;
    font-family: 'Source Code Pro', 'Roboto Mono', 'Lucida Console', monospace;
    
}
.column.has-text-centered {
    font-size: 0px; /* hiding progressbar */
}
.card-content {
    background-color: #191919;
    padding:25px 30px 15px;
}
.title.is-5 {
    font-size:14pt;
    font-weight:bold;
    color:#cdbae5;
}
.subtitle.is-6 {
    font-size:10pt;    
    color:#cacaca
}
.select select {
    background-color:#212121;
    border:none;
    border-radius:0;
    color:#acacac;
    font-family: 'Source Code Pro', 'Roboto Mono', 'Lucida Console', monospace;
    font-size:10pt;
    margin-top:-10px;
    margin-left:-1px;
}
.select select.is-active, .select select.is-focused, .select select:active, .select select:focus {
    box-shadow: none;
}
.select:not(.is-multiple):not(.is-loading)::after {
    margin-top:-20px !important;
    margin-right:-3px !important;
    border-color:#cdbae5
}

/* deleting content */
.title.is-4, p, .title.is-5, li {
    display:none;
}
/* restoring content */
.card-content .title.is-5, .column p, .footer li {
    display:block;
}

hr {
    margin-top:1px;
    height:2px;
    background-color:#191919
}
.footer {
    background: #00000000;
    padding:0;
}
.pagination-list {
    margin-top:-80px;
    text-transform:lowercase;
}
.pagination-link {
    font-size:8pt;
}
.pagination-link, .pagination-next, .pagination-previous,.pagination-link:focus {
    border-color:#cdbae5a0;
    color:#acacac;
}
.pagination-link:hover, .pagination-next:hover, .pagination-previous:hover {
    border-color:#cdbae5a0;
    color:#acacacac;
}
.pagination-link.is-current {
    background-color:#cdbae5;
    border-color:#cdbae5;
    color:#212121;
}

