css:
/* by:Carlos Parisaca*/

@-moz-document domain(cell.sh){

#mainmenu {

    width: 900px;
    height: 600px;
    border-radius: 15px;
    -webkit-backdrop-filter: blur(15px);
    backdrop-filter: blur(15px);
    background-color: rgba(243,243,243,0.2);
    box-shadow: 0 10px 30px #000;
}
.cshtabs li a{
color:white
  }

nav{
	-webkit-backdrop-filter: blur(15px);
    backdrop-filter: blur(15px);
	background-color: rgba(255, 255, 255, 0);

}

#menu_left, #menu_right {

    background-color: #de0a0a00;
}

#mainmenu .menu_red_footer{
	-webkit-backdrop-filter: blur(15px);
    backdrop-filter: blur(15px);
	background-color: rgba(255, 255, 255, 0);
}

#mainmenu #menu_right{
	border-right: none;
}

#mainmenu #menu_left{
	border-right: none;
}

.teal.darken-1{
	background-color: #0ea017 !important;
}

.cshtabs li a {
    color: rgb(255, 255, 255);
}

#mainmenu #account-xplevel #level-pellet {
    width: 45px;
    height: 45px;
    border-radius: 100px;
    border: 4px solid rgb(216, 178, 14);
    background-color: rgb(255, 230, 0);
    font-size: 20px;
    text-shadow: 1px 1px #000;
    color: black;
}
#nick{
	color: white;
}

#dmcarequests {
background-color: #f1f3f300;
}

#changelog{
background-color: #f1f3f300;
}

#levelWindow{
background-color: #f1f3f300;
}

#captchaV{
background-color: #f1f3f300;
}

#highScoreWindow {
background-color: #f1f3f300;
}
#shop{ 
	border-radius: 15px;
background-color: #e2dddda6;}
#popupsOverlay{

	-webkit-backdrop-filter: blur(15px);
    backdrop-filter: blur(15px);
	background-color: rgba(255, 255, 255, 0);

}

#shop .cshtabs li a {
    color: black;
}

#shop .cshtabs li:hover a {
    color: rgb(4, 138, 8);
}

#shop .cshtabs .active{
	    border-bottom: 2px solid rgba(13, 115, 11, 0.7);
}

.dropdown-content{
	 border-radius: 0 0 15px 15px;
	-webkit-backdrop-filter: blur(15px);
    backdrop-filter: blur(50px);
    background-color: rgba(33, 33, 33, 0.44);
}

.dropdown-content li>a, .dropdown-content li>span{
	color: #ffffff;

}
.dropdown-content li>a, .dropdown-content li>span:hover{
	background-color: #2f2f2f54;
}

}
