css:
@-moz-document url-prefix("https://sephir.ch/ICT") {
html > body,
#whitespace,
table.listtab_rot > tbody > tr:nth-child(even) {
  background: #505050 !important;
}

.content,
table.listtab_rot > tbody > tr:nth-child(odd) {
  background: #606060 !important;
}

table,
.funcbar_main_end,
.funcbar_main_start,
.tdschwarz10px,
table.listtab_rot > tbody > tr > td > div#ebfach,
select.tdschwarz {
  background: #707070 !important;
}

div > table:hover,
.funcbar_main_start:hover,
.funcbar_main_full:hover,
.funcbar_main_end:hover,
.tabregister_start,
.tabregister_mid,
.tabregister_end,
table.listtab_rot > tbody > tr > td > div#ebfach:hover {
	background: #808080 !important;
}

input.tdschwarz {
  color: #000 !important;
}

#trennleiste,
#aktivmarker,
.tdschwarz10px > img,
div.content > div > a > div > img,
table.tabresp > tbody > tr > td:last-child > div > div.tdschwarz10px:first-child {
	display: none !important;
}

html > body,
td,
td > a,
table,
div > a,
.content,
.aktiv,
.inaktiv,
.titel,
.tdschwarz,
.tdschwarz10px,
.tdgrau,
#navi a,
.content > div > table > tbody > tr > td,
.content > div:first-child > table > tbody > tr > td,
.funcbar_text,
.selecttitel,
select.tdschwarz {
  color: #f0f0f0 !important;
}

.navbox > .content > div:first-child {
	padding-top: 0 !important;
}

.navbox:last-of-type > .content > div:first-child {
	padding-top: 10px !important;
}

.tabschatten,
.navbox,
.box {
  box-shadow: 3px 3px 6px #303030;
}

#aktivmarker {
  margin-left: -2px;
  width: 100%;
  left: 0;
  padding: 0 2px;
}

.aktiv,
#aktivmarker {
  background: #737373;
}

.aktiv:hover,
.inaktiv:hover {
  background: #888888;
}

#navi > .aktiv {
  letter-spacing: 0;
}

#navi > div {
  padding: 3px 3px 5px;
}

.titel {
  background: #444444 !important;
}

.content > div:first-child > table > tbody > tr > td > img {
  width: 80px;
}

.content > div:first-child > table > tbody > tr > td {
  background: inherit !important;
}

.content > div > table > tbody > tr > td {
  background: #585858 !important;
}

#lelist {
  background: inherit !important;
}

body > table {
  background: #505050 !important;
}

div.divbox_rot > div.content > div > table {
  background-color: #606060 !important;
}

table.tabresp > tbody > tr > td:last-child > div > div.tdschwarz10px:nth-child(2) {
  padding-left: 5px !important;
  padding-right: 5px !important;
  border-top: 1px solid #000 !important;
  border-left: 1px solid #000 !important;
  border-right: 1px solid #000 !important;
  border-top-left-radius: 5px;
  cursor: pointer;
}
}
