css:
@-moz-document domain(community.gamedevhq.com) {
	body {
	background: #011A1D;
	color: #e9e9e9;
}
nav.ui.borderless.text.main-menu.menu {
	background: #03272b;
}
.icon {
	color: #e9e9e9;
}
span.mobile.hidden {
	color: #ababab;
}
input.prompt {
	background-color: #222 !important;
	border: 1.5px dotted grey !important;
}
.ui.basic.button, .ui.basic.buttons .button {
	color: rgba(255, 255, 255, 0.6)!important;	
	background: #2399AC!important;
	box-shadow: inset 0 0 0 1px #3ee4ff;
}
.ui.comments .comment a.author {
	color: #73E4EA;
}
.ui.comments .comment a.author:hover {
	color: #e9e9e9;
}
.ui.comments .comment .text, .ui.menu .item {
	color: #adadad;
}
.ui.card {
	background: #121212;
	color: #999;
	border: 1px solid #222;
}
.post-comment-wrapper {
	border-top: 1px solid #444 !important;
}
.description {
	color: #9E9E9E;
}
.description a {
	color: #73E4EA;
}
.user-bar .user-bar-name a {
	color: #696969;
}
.user-bar .user-bar-name .meta {
	color: #F09941;
}
div textarea {
	background: #222 !important;
}
.ui.segment a {
	color: #b1b1b1;
}
.ui.segment {
	background: #111111;
	color: #b1b1b1;
	border: 1px solid #222222 !important;
}
.ui.segments {
	border: 1px solid #222222 !important;
}
.ui.segments>.segment {
	border-top: 1px solid #222222 !important;
}
.ui.header {
	color: #888888;
}
h3 a {
	color: #56aeb3 !important;
}
.content-input {
	background: #222;
}
.user-todos .statistic .value {
	color: #989898;
}
.user-todos .statistic .label {
	color: #888;
}
.ui.list>.item .header {
	color: #989898;
}
.ui.statistic>.value {
	color: #989898;
}
.ui.statistic>.label {
	color: #666;
}
.link-preview-text {
	background: #222;
	border: 1.5px dashed #555;
}
.link-preview-title {
	color: #73E4EA;
}
.link-preview-url {
	color: #555;
}
.link-preview-description {
	color: #999;
}
.content-input {
	border: 1px solid #444;
}
.question-title {
	color: #449ca5;
}
.avatar-group span {
 	border: 2px solid #333;
}
  .user-mention {
	background: #e09f08;
    color: white;
  }
}
