css:
@-moz-document domain("forums.blizzard.com") {
/* stuff you may want to change */

/* hide junk i don't like */
/* suggested topics */
.NavbarFooter,
#suggested-topics {
    display: none;
}
/* frequent posters, summarize this topic, etc */
.topic-map {
    display:none !important;
}
/* the "welcome to our community" message */
#list-area > .alert-info:nth-child(2) {
    display: none;
}

.character-achievement-points {
    display:none !important;
}
h5.user-post-count {
   display:none;
}
/* font */
body {
    font-size:15px;
}
p, li {
    font-family: Tahoma;
     font-size: 14px;
}

/***************/
 
.desktop-view .navigation-container {
    margin-top: 1em;
}
.subcategory-container > a {
    padding: 5px;
    font-size: 13px;
    margin-bottom: 0.1em;
}
.category-boxes {
    margin: 0.5em 0;
}
.category-boxes .subcategory-container {
    width: initial;
    padding: 0.1em !important;
}
.category-links li,
.menu-links li {
    font-size: 14px;
}
.category-name {
    text-transform:none;
}
.topic-list-item .main-link {
    font-size: 14px;
}
.topic-list-item > td {
    padding: 0 5px;
    font-size: 13px;
}
.topic-list-item > td.category span {
    /*
max-width: 30em;
   direction: rtl;
overflow: hidden;
    text-overflow:ellipsis;
    white-space:nowrap;
    */
}
.d-tooltip {
    top: 0.5em !important;
}
.topic-list-item > .main-link {
    padding: 2px !important;
}
.select-kit-collection > .select-kit-row {
    font-size: 14px !important;
}
#navigation-bar {
    margin-top: 0;
}
#navigation-bar > li > a {
    padding: 5px;
    font-size: 14px;
}

#create-topic {
    font-size: unset !important;
}
.topic-list tr.table-break td {
    padding: 0;
}
/* thread */
#topic-title {
    padding-top:1em !important;
}
#topic-title h1 {
            font-size: 23px;
    text-transform: none;
}
section.information p {
    font-size: 14px;
}
section.post-menu-area {
    font-size: 13px;
}
.topic-map .links,
.topic-map .information,
.topic-map .avatars {
    padding: 5px 0;
    font-size: 14px
}
.topic-post  {
    min-height: unset !important;
}
.topic-post .topic-avatar {
    padding-top: 10px !important;
}


.topic-post .secondary-details {
    font-size: inherit !important;
}
.topic-post .post-actions {
    margin-bottom: 0 !important;
}
button.btn-primary {
    padding: 4px 10px !important;
}
.topic-body {
    padding: 5px !important;
}
.topic-body .character-details * {
    font-size: 14px;
}
.post-menu-area {
    margin-top: 5px !important;
}

.alert-info p,
.alert-info a,
.alert-info .btn {
    font-size: 14px;
}
.timeline-container  {
    font-size:10px;
}
.timeline-container .topic-timeline .timeline-scrollarea {
 margin: 1em;
}
.timeline-container .topic-timeline .timeline-padding {
    transition: height 0.01s linear;
    cursor: pointer;
}


}
