css:
@-moz-document domain("autogaleria.pl") {
html,body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #094225 #1c1c1c;
}
a{
    color: #facf00;
    opacity: 1;
}
/*HEADER*/
.submenu,.brands-box{
    background: #1c1c1c !important;
    box-shadow: none !important;
    border: 1px solid #facf00;
}
.s-nav-item:hover,.brand-line-item:hover{
    color: #1c1c1c;
}
.brand-item:hover{
    border: 1px solid #facf00;
}
.brand-item__name, .brand-line-item{
    color: #facf00;
}
.brands-box__aside-title{
    color: #fff;
}
/*HEADER*/
/*STRONA-GŁÓNWA*/
.p-row__inner,.p-tile__inner,.p-item__inner,.c-label--badge{
    background: #094225 !important;
}
.p-tile-title,.p-meta__author-name,.p-meta__date,.hot-menu__item,.thread__time{
    color: #fff;
    opacity: 1;
}
.c-label,.title-1{
    color: #facf00;
    opacity: 1;
}
.f-title__tag,.f-title__meta{
    background: #1c1c1c !important;
    color: #facf00;
}
.thread{
    background: #1c1c1c;
}
/*STRONA-GŁÓNWA*/
/*ARTYKUŁ*/
.breadcrumb-item a,.post__content h2,th,.tag:hover{
    color: #facf00;
}
.breadcrumb-item{
    color: #fff;
    opacity: 1;
}
.post__lead,.post__content p,.post__content table td,.post__content h4{
    color: #fff;
}
.post__content table tr:nth-child(2n),.rating--pros{
    background: #094225;
}
.rating--cons{
    background: #cb000042
}
.rating--pros .rating__header svg path{
    fill: green;
}
.rating ul li::before,.tag,.p-mini__title{
    color: green;
}
.p-meta__author-name{
    color: #facf00;
    opacity: 1;
}
.post{
    border-color: #094225
}
.post__content a,.post__content p a{
    background: #1c1c1c;
}
/*ARTYKUŁ*/
/*PODSTONY*/
.btn:hover{
    background: #1c1c1c;
    border: 1px solid #facf00;
    color: #facf00;
}
.title-caption{
    color: #fff;
    opacity: 1;
}
/*PODSTONY*/
/*FOOTER*/
.footer{
    background: #094225;
}
.footer:before{
    content: "Ciemny motyw by maciejozaman";
    margin-left: 25px;
    color: #facf00;
}
/*FOOTER*/
}
