css:
@-moz-document domain("twitch.tv") {
.tw-root--theme-dark body {
    background-color: #1b1b1b;
}

body {
    font-family: Roboto,sans-serif !important;
}

.top-nav__menu.tw-align-items-stretch.tw-c-background-base.tw-elevation-1.tw-flex.tw-flex-nowrap.tw-full-height {
    background: #9147ff !important;
}

.tw-c-background-base {
    background-color: #383838!important;
}

.tw-root--theme-dark .top-nav__external-link:hover {
    color: #000000;
}

.tw-root--theme-dark .navigation-link.active, .tw-root--theme-dark .navigation-link:hover {
    color: #000000;
}

.tw-animated-glitch-logo__body {
    fill: #000000;
}

.tw-root--theme-dark .channel-root__right-column {
    background: #1b1b1b;
}

.tw-root--theme-dark .chat-room {
    background: #1b1b1b;
}

strong.cheermote-for-amount__cheer-amount.cheermote-for-amount__cheer-amount--extremely-small {
    color: #fff !important;
    padding: 2px;
}

.tw-root--theme-dark .channel-header {
    background-color: #1b1b1b;
}

.tw-root--theme-dark .persistent-notification__unread {
    background-color: #1b1b1b;
}

.persistent-notification.tw-relative {
    background: #0e0e0e;
}

svg.tw-svg__asset.tw-svg__asset--alt-2.tw-svg__asset--logoglitch {
    fill: hsl(262, 45%, 45%);
}

.prime-offer-list__content.prime-offer-list__content--more-offers-to-see.prime-offer-list__content--vertical.tw-align-items-center.tw-pd-b-2.tw-pd-t-1.tw-pd-x-1 {
    background: #1b1b1b;
}

.tw-tag {
    background-color: hsl(0, 0%, 7%);
}

.tw-root--theme-dark .chat-viewers__pane {
    background: #1b1b1b;
}

.tw-textarea {
   background-color: hsl(0, 0%, 6%);
}

.tw-textarea:focus {
   background-color: #272727;
}

.tw-root--theme-dark .directory-header-new__banner-cover {
    background: linear-gradient(0deg,#1b1b1b,rgba(0, 0, 0, 0.27)),linear-gradient(90deg,#9147ff,rgb(0, 0, 0));
}

.tw-input:focus {
    background-color: #272727;
}
}
