css:
@-moz-document url-prefix("http://jira"), url-prefix("https://jira"), domain("jira.atlassian.com") {
body, html {
     min-height: 100%!important;
}
html, body {
     background-color:#2d2d2d!important
}
body>*:not(:empty):not(:svg) {
     background-color:#36393F!important;
}
svg {
	background: none;
    
}
.canvas svg text  {
 	fill: black;   
}
body>*>*:not(:empty) {
     background-color:#36393F;
}
.aui-inline-dialog-contents {
     border: 1px solid #2d2d2d !important;
}
body>*>*>*:not(:empty) {
     background-color: #36393F;
}
body>*>*>*>*:not(:empty) {
     background-color:#36393F!important
}
body>*>*>*>*>*:not(:empty) {
     background-color:#36393F!important
}

#ghx-pool, #ghx-column-header-group {
    background-color:#36393F!important
}

#quicksearch-menu form {
    width: 650px;
    padding-left: 0px !important;
    padding-right: 0px !important; 
}
#quickSearchInput {
 	width: 100%;   
}

.quicksearch-dropdown {
width: 100% !important;
    border: solid 1px !important;
    left: 0px !important;
}

/* this breaks upload box */
 body>*>*>*>*>* *:not([class^="tm-label-circle"]):not([class^="tm-status-dropdown"]):not([class^="ghx-grabber"]):not([class*="ghx-active"]):not([class*="aui-icon"]):not([id^="attachmentmodule"]):not([class^="issue-drop-zone"]):not([class$="highlight"]):not([class="label"]):not([class="aui-inline-dialog-contents"]):not([class="aui-sidebar-submenu"]):not([class="aui-navgroup-inner"]):not([class="aui-nav-heading"]):not([class="aui-nav collapsed-scope-list"]):not(strong):not([class="scope-filter"]):not([class="selected-scope-filter"]){
     background-color:#36393F;
}

.spinner svg {
 	background: none !important;   
}

.aui-inline-dialog-contents, .aui-inline-dialog-contents>*>*>*, .aui-inline-dialog-contents>*>*>*>*, .aui-inline-dialog-contents>*>*>*>*>*, .aui-inline-dialog-contents>*>*>*>*>*>*  {
 	background: #2d2d2d !important;   
}

.tipsy .aui-tooltip .aui-sidebar-section-tooltip .tipsy-w .tooltip-shown {
    border: solid 1px !important;
    background-color: none;
}
.aui-tooltip.tipsy, .tipsy {
 	padding: 0px;
    
}
.tipsy .tipsy-inner {
 	border: solid 1px;
}


body *:empty:not([class*="icon"]):not([class^="tm-"]):not([class^="ghx-grabber"]):not([id^="attachmentmodule"]):not([class^="issue-drop-zone"]) {
     /*background-color: #0000005e !important;*/
}
body table:not(:empty) {
     background-color:#36393F!important;
}
body table>tbody>tr:nth-child(even) {
     background-color:rgba(0, 0, 0, .2)!important;
}
body table>tbody>tr>td:nth-child(odd) {
     background-color:rgba(0, 0, 0, .1)!important;
}
body p:not(:empty), body p *, body h1, body h1 *, body h2, body h2 *, body h3, body h3 *, body h4, body h4 *, body h5, body h5 *, body strong>*, body b>*, body em>*, body i>*, body span>*:not(img) {
     background:transparent none!important
}
body h1, body h1 *, body h2, body h2 *, p>strong:only-of-type, p>b:only-of-type {
     color: #a98!important
}
body h3, body h3 *, body h4, body h4 * {
     color: #aaa!important
}
*:not([onclick]):not(input):not(a):not(img):not([class^="UI"]):not([class^="tm-"]):not([class*="icon"]), body a:not(:empty):not([class^="inplace_"]):not([class*="tempo-actions-trigger"]), div:not([onclick]):not([class^="tm-"]):not([class*="icon"]) {
     background-image:none!important;
}
*[onclick] {
     color:#79a!important
}
*[onclick]:hover {
     color:#99a8aa!important
}
body hr {
     background: #666 none!important;
     color: #666!important;
     border:1px solid #666!important;
     height: 1px!important;
     overflow:hidden!important;
     display: block!important
}
*:not([class="label"]) {
     color: #c0c0c0!important;
     border-color:#60666a!important;
     box-shadow: 0 0 0 #333!important
}
* body a, body a * {
     color: #B6AA7B!important;
}
/* body a:visited, body a:visited *{color: #666!important;} */
 body a:hover, body a:hover * {
     color: #D9C077!important;
     text-decoration: underline!important
}
/* body img,a[href] img, a[href] button, input[type="image"],*[onclick]:empty, body a:empty{opacity:.5!important} */
 body img:hover, a[href]:hover img, a[href]:hover button, *[onclick]:empty:hover, body a:empty:hover {
     opacity:1!important
}
body span>img {
     visibility:visible!important
}
body input[type], input, body textarea, body select {
     -moz-appearance:none!important;
     color: #bbb!important;
     border-radius:4px !important;
     border-width: 1px!important;
     border-color:rgba(100, 100, 100, .5)!important;
     border-style:solid!important;
     background:-moz-linear-gradient(top, #303030 40%, #36393F, #343434) #333 !important;
     box-shadow: 1px 1px 1px #111!important
}
body input[type="submit"], body input[type="button"], body input[type="image"], body input[type="reset"], body button {
     -moz-appearance:none!important;
     color: #bbb!important;
     border-radius:4px !important;
     border-width: 1px!important;
     border-color:rgba(100, 100, 100, .5)!important;
     border-style:solid!important;
     background:-moz-linear-gradient(top, #404040 45%, #282828 55%) #333 !important;
     box-shadow: 1px 1px 1px #111!important
}
body input[type]>*, body textarea>* {
     -moz-appearance:none!important;
     background:transparent none !important;
     color: #bbb!important;
     border-style:solid!important;
     border-width: 0px!important;
}
body select * {
     background-color:transparent !important;
     color: #bbb!important;
     border-style:solid!important;
     border-width: 0px!important;
}
pre:not(:empty), code:not(:empty), cite:not(:empty), pre:not(:empty) *, code:not(:empty) *, cite:not(:empty) * {
     background-image:url(data:image/gif;base64,R0lGODlhBAAEAIAAABERESIiIiH5BAAAAAAALAAAAAAEAAQAAAIGTACXaHkFADs=)!important;
     color: #bcc8dc!important;
}
.aui-toolbar .toolbar-group .toolbar-trigger, .aui-toolbar .aui-dd-parent a.aui-dd-trigger {
     text-shadow: 0 0px 0 white;
}
.aui-inline-dialog .buttons-container.content-footer input.cell-add {
     background: #000!important;
}
/*
.aui-sidebar .aui-nav>li>.aui-nav-item>.aui-icon, .aui-sidebar-submenu .aui-nav>li>.aui-nav-item>.aui-icon {
    filter: invert(100%);
    -webkit-filter: invert(100%);
}
*/
 a.js-quickfilter-button.last.ghx-active, a.js-quickfilter-button.first.ghx-active, a.js-quickfilter-button.ghx-active, .ghx-controls-filters dd a.ghx-active {
     background: #3572b0!important;
}
.editable-field .overlay-icon {
     color: transparent!important;
}
.tm-wc-item:hover .editable-field .overlay-icon:after, .tm-description:hover .editable-field .overlay-icon:after {
     content:"\f142";
     color: #b6aa7b;
     font-family:"Atlassian Icons";
     left: 10px;
     position: absolute;
     top: 10%;
}
.ghx-swimlane-header:after, .ghx-swimlane-header.ghx-selected:after {
     visibility:hidden;
}
.ghx-grabber {
     filter: brightness(60%);
     -webkit-filter: brightness(60%);
}
.tm-label-circle {
     filter: brightness(60%) contrast(300%);
     -webkit-filter: brightness(60%) contrast(300%);
}
.tm-status-dropdown {
     filter: brightness(60%) contrast(150%);
     -webkit-filter: brightness(60%) contrast(150%);
}


/* scroll bars */


::-webkit-scrollbar {
     width: 10px;
     height: 10px;
     /* This is more usable for users trying to click it. */
     background-color: #5F636E;
     -webkit-border-radius: 100px;
}
/* hover effect for both scrollbar area, and scrollbar 'thumb' */
 ::-webkit-scrollbar:hover {
     background-color: rgba(125, 125, 125, 1);
}
/* The scrollbar 'thumb' ...that marque oval shape in a scrollbar */
 ::-webkit-scrollbar-thumb:vertical {
     /* This is the EXACT color of Mac OS scrollbars. 
     Yes, I pulled out digital color meter */
     background: rgba(0, 0, 0, 0.5);
     -webkit-border-radius: 100px;
}
 ::-webkit-scrollbar-thumb:horizontal {
     /* This is the EXACT color of Mac OS scrollbars. 
     Yes, I pulled out digital color meter */
     background: rgba(0, 0, 0, 0.5);
     -webkit-border-radius: 100px;
}
::-webkit-scrollbar-thumb:vertical:active {
     background: rgba(0, 0, 0, 0.61);
     /* Some darker color when you click it */
     -webkit-border-radius: 100px;
}

::-webkit-scrollbar-corner {
  background: transparent;
}


#page[aria-hidden="true"] {
    filter: blur(3px);
}

.aui-blanket {
 	background: none !important;   
}


.aui-button.aui-button-link {
     padding-left: 10px !important;
}
.filter {
     -webkit-border-radius: 3px 3px 3px 3px !important;
     border-radius: 3px 3px 3px 3px !important;
     padding: 4px 10px;
     box-shadow: none !important;
     width: 330px;
}
.highlight {
     background-color: #b69f58 !important;
     color: #36393F !important;
     box-shadow: none !important;
}
input:focus {
     outline: none;
}
button:active {
     outline: none;
}
body:not(.aui-legacy-focus) .aui-header a:focus:before {
     box-shadow: none !important;
}
.jira-dialog {
 	background: none;   
}
.jira-dialog .buttons-container {
	border-top: none;
    background: #36393F;
}
.jira-dialog .jira-dialog-content {
 	background: none;   
}

.aui-button.aui-button-link, .aui-button.aui-button-link:visited, .aui-button.aui-button-text, .aui-button.aui-button-text:visited {
 	padding: 4px 10px !important;  
}

.buttons-container.buttons-container.buttons-container {
    margin-top: 0px;
}
.aui-tabs.horizontal-tabs>.tabs-menu:before {
 	z-index: 1;   
}

.aui-navgroup .aui-navgroup-horizontal .editor-toggle-tabs {
	margin-top: 0px !important;
}

#gh:not(.ghx-sticky-sprint-headers-disabled) .ghx-backlog-container .ghx-backlog-header {
	background: none;    
}
.aui-button  svg {
    background: none !important;
}

.ghx-backlog-container {
 border-bottom: none !important;   
}
.aui-button.ghx-expander.ghx-heading-expander {
   height: 16px !important;   
}

.aui-dropdown2.aui-style-default {
 	background: none;
    border: 1px solid #dfe1e6;
}

.aui-dropdown2.aui-style-default .aui-dropdown2-section {
 	margin-top: 0px;
    padding-top: 3px;
}
/* Modifications to "Improved Jira" extension (https://chrome.google.com/webstore/detail/jira-improved/mdfbpeoaadkecmpingophakekbicinip) */

.ghx-column-headers .ghx-column, .ghx-columns .ghx-column {
     border: solid 1px;
     border-radius: 8px;
     padding: 5px 10px 5px 10px !important;
     margin-top:5px;
}
.ghx-swimlane-header {
     top: 29px !important;
     margin: 5px 0px !important;
}
.ghx-issue {
     margin: 5px !important;
     border: solid 1px;
     border-radius: 2px;
     box-shadow: 1px 1px 1px #111!important;
}

.ghx-issue-content {
     border-radius: 10px !important;
}

#ghx-operations {
    padding: 0 10px !important;   
}

.ghx-column {
    padding: 0px 10px 0px 10px !important;
}

.jira-dialog-heading {
     background-color: #252525 !important;
     border-width: 1px !important;
}
.label {
 	font-size: 12px !important;   
}
.ghx-release {
	font-size: 12px !important;   
}

.ghx-flags {
 opacity: 1 !important;   
}
}
