css:
@-moz-document domain(angular.io) {
	/* your code goes here */
  a, input, li, ol, p, ul, table thead>tr>th{
    color:#eaeaea;
}
.content, body {
    background-color: #212a3a;
}
mat-sidenav.mat-sidenav.sidenav{
    background:#171d27;
}
.level-1, .vertical-menu-item{
    color:#e2e2e2;
}
.vertical-menu-item:hover {
    background-color: #212a3a;
    color: #cecece;
    text-shadow:none;
}
mat-sidenav-container .sidenav-content, mat-sidenav-container.sidenav-container.mat-drawer-container.mat-sidenav-container {
    background-color: #212a3a;
}
h1, h2, h3, h4, h5, h6 {
    color: #f3f3f3;
}
.code{
    color:#a5a5a5;
}
.clo, .opn, .pun {
    color: #cecece;
}
code {
    color: #bfbfbf;
}
.kwd{
    color:#dd98f2;
}
.str {
    color: #9ccc65;
}
.typ{
    color:#eceff1;
}
.pln {
    color: #4dd0e1;
}
code-example:not(.no-box), aio-code pre{
    background: #283142;
    color: #eceff1;
}
.tag{
    color:#e06c70;
}
table{
    background: #171d27;
    color: #fff;
}
.atn{
	color:#de9030;
}
.atv{
	color:#60bf63;
}
.toc-inner ul.toc-list li a{
    color:#bfbfbf;
}
.com{
    color:#0cbd0c;
}
.alert.is-helpful, .is-helpful.callout{
    background:rgba(25,118,210,0.2);
}
.alert.is-critical, .is-critical.callout {
    background-color: rgba(221,0,49,0.3);
}
code-example:not(.no-box){
    border-color:#1e88e5;
}
.alert, .callout{
    background: #273246;
    color:#cacaca;
}
.sidenav-content code a, .sidenav-content code a:hover{
    color:#4CAF50;
    text-decoration:none;
}
.sidenav-content code a:hover{
    color:#55c35a;
}
}
