css:
@-moz-document domain("www.malaysiakini.com") {
body , html{
    background-color: #111;
    font-family: san-serif;
}
.at4-share {
    display:none;
    background-color: #000;
}
.commercial-space {
    margin-bottom:-50px;
    background-color: #000;
}
.ad-unit-LB-1, .ad-unit-LB-2 {
    margin-bottom:-110px;
    background-color: #000;
}
.main-header, .fb-reset {
    font-family: san-serif;
     background-color: #000000bd;
    border:0
}
#article_content p, #article_content ul, #article_content ol, #article_content dl {
    color:#777;
    font-size: 16px;
    font-weight: 400;
    font-family: san-serif;
}

.opinion-card .quote-card .quote {
    padding: 15px;
    background-color: #000;
    border:0 !important;
}

.opinion-card a .quote .quote-big {
    color: #FF6F00 !important;
}

.opinion-card a:hover .quote .quote-big {
    text-decoration: underline !important;
}


.opinion-card .quote-card .quote .quote-title {

    font-size: 15px;
    font-weight: 400;
    color: #fff;
    padding-bottom: 10px;
    margin-bottom: 90px;

}


a h3 {
    color:#fff !important;
    font-family: san-serif;
}

a {
    color:#fff !important;
    font-family: san-serif;
    color: #fff !important;
}

a:hover {
   color: #FF6F00 !important;
    font-family: san-serif;
    text-decoration: underline !important;
}

.uk-h1, .uk-h2, .uk-h3, .uk-h4, .uk-h5, .uk-h6, .uk-heading-2xlarge, .uk-heading-large, .uk-heading-medium, .uk-heading-small, .uk-heading-xlarge, h1, h2, h3, h4, h5, h6 {
    color: #ff6c00
}
.side-bar .side-bar-container ul {
    background-color: #000000;
    padding: 10px;
    list-style: none;
    font-size: 14px;
    border-radius: 35px;
}
}
