css:
@-moz-document domain("textnow.com") {
body.app {
    background-color: #020202;
}

.messaging-v2 .side {
    background: #020202;
    box-shadow: inset -1px 0 0 0 #ededee;
}

#appContainer #chatContainer .content {
    background-color: #020202;
}

#recent-header {
    border-bottom: 1px solid #dce5ec;
    color: #dedede;
    background-color: #020202;
}

.uikit-summary-list__cell--active, .uikit-summary-list__cell:active {
    background-color: #020202;
}

.uikit-summary-list__cell-text1 {
    color: #dedede;
}

.uikit-text--muted {
    color: #dedede;
}

.uikit-summary-list__cell {
    color: #dedede;
}

.uikit-summary-list__cell:focus, .uikit-summary-list__cell:hover {
    background-color: rgba(75, 74, 78, 0.5);
}

.messaging-v2 .responsive-enabled #recent-header.new-header>div:first-child>.phoneNumber {
    color: #dedede;
}

.messaging-v2 .responsive-enabled #recent-header.new-header>div:first-child>.name {
    color: #dedede;
}

.messaging-v2 .responsive-enabled .chat .profile-toolbar .new-header .contact-name {
    color: #dedede;
}

.messaging-v2 .responsive-enabled #sidebarNavContainer .sidebar-nav {
    background-image: linear-gradient(rgb(47, 47, 47) 0px, rgb(21, 21, 21) 100%);
}

.messaging-v2 .responsive-enabled .chat .profile-toolbar .new-header .conv-header-container {
    border-bottom: 1px solid #484848;
}


.messaging-v2 #chatContainer #sidebar {
    border-left: 1px solid #464646;
}

.chat .input #text-inner, .group .input #text-inner {
    background: #020202;
}

.messaging-v2 .responsive-enabled .chat .input {
    background: #020202;
    border-top: 1px solid #4a4a4a;
}

.chat .input textarea::-webkit-scrollbar, .group .input textarea::-webkit-scrollbar {
    background: #02020200;
}

button, html, input, select, textarea {
    font-family: sans-serif;
    color: #dedede;
}

.uikit-chat-item .uikit-chat-item__bubble--incoming {
    background: #2d2d2d;
    transition: background .4s linear;
}

.messaging-v2 .chat-item.incoming .message .message-item .message-content, .messaging-v2 .chat-item.incoming .message .message-item .message-sender {
    color: #dedede;
}

.uikit-chatview__timestamp {
    color: #dedede;
}

.messaging-v2 .responsive-enabled .chat .profile-toolbar .profile-menus .call-pill, .messaging-v2 .responsive-enabled .group .profile-toolbar .profile-menus .call-pill {
    color: #8149f8;
    border: 2px solid #8149f8;
    background: #2d2d2d;
}

.messaging-v2 .responsive-enabled #recent-header.new-header {
    border-right: 1px solid #484848;
    border-bottom: 1px solid #484848;
}

#page-loading {
    background: #000;
}

#page-loading .light-loader {
    background: none;
}

.chat .input.disableSpan #text-inner, .chat .input.disableSpan textarea, .group .input.disableSpan #text-inner, .group .input.disableSpan textarea {
    background-color: #020202;
}
}
