    color: #FFF;
}
.likesYou__scroller div :after {
    transform: none !important;
}
.Blur\(12px\)\:\:a::after {
    blur: none !important;
    -webkit-filter: none !important;
}

.recsCardboard {
        max-width: calc(100vw - 65vw);
        height: calc(100vh - 15vh);
        max-height: calc(100vh - 15vh);
    }

