css:
/* i really want this to be global */
#bjd_bottom_detail,
#gwd_website_icon,
#mini_price_history_detail .app-tuiguang,
#gwd_mini_remind,
#gwd_mini_seckill,
#trend_com_detail {
    display: none !important
}

#gwd_minibar .minibar-tab {
	border-left-width: 0 !important;
}

#mini_price_history_detail {
	top: 36px;
	border-top: 1px solid #ddd;
}

#big_tooltip {
	width: 100%;
}

#mini_price_history_detail .mini_price_history_detail_wrapper {
	width: 100%;
}

.gwd-minibar-bg {
	padding-top: 5px;
}

#gwd_minibar {
	height: 34px;
	line-height: 34px;
}

#gwd_minibar .minibar-tab {
	height: 34px;
}

#mini_price_history {
	text-align: left;
	cursor: pointer;
	width: 95px !important;
	padding-left: 5px !important;
}

#gwd_minibar + .dsj-content {
	top: 6px;
	left: 100px;
	user-select: none;
	position: absolute !important;
}

#gwd_minibar + .dsj-content .dsj-promo-link {
	cursor: pointer !important;
}

#gwd_minibar + .dsj-content .dsj-promo-link {
	width: 182px !important;
}

#gwd_minibar + .dsj-content .dsj-promo-link > div {
	border-top: 1px solid #edf1f2;
	top: 36px !important;
}

#gwd_mini_compare {
	cursor: pointer;
	z-index: 100 !important;
	flex-grow: 0 !important;
	flex-shrink: 0 !important;
	flex-basis: 155px !important;
}

@-moz-document domain("jd.com") {
/* 修复京东导航栏宽度异常问题 */
#shop-head {
	overflow: hidden;
}
.userDefinedArea {
	width: 100%;
}

#gwd-price-protect,
.gwd-middle-act-bar,
.jdm-tbar-tab-qrcode,
.bjgou-subsidy-bar {
	display: none !important;
}

#gwd_minibar {
	width: 588px;
}

#gwd_minibar + .dsj-content {
	position: absolute !important;
	width: 488px !important;
}

#gwd_minibar + .dsj-content .dsj-promo-link {
	width: 270px !important;
}

#gwd_minibar + .dsj-content .dsj-remind-icon{
	display: none !important;
}

#gwd_mini_compare_detail {
	left: 309px !important;
}
}

@-moz-document domain("jd.hk") {
.dsj-content {
	width: 403px;
	position: absolute !important;
}

#gwd_minibar {
	width: 503px;
}
}

@-moz-document domain("taobao.com") {
#gwd_minibar {
	width: 460px;
}
}

@-moz-document domain("tmall.com") {
#gwd_minibar {
	width: 508px;
}
}

@-moz-document domain("suning.com") {
#gwd_minibar + .dsj-content {
	width: 360px !important;
}

#gwd_minibar + .dsj-content .dsj-promo-link .dsj-text,
#gwd_minibar + .dsj-content .dsj-promo-link img,
#gwd_minibar + .dsj-content .dsj-remind-icon{
	display: none !important;
}

#gwd_minibar + .dsj-content .dsj-promo-link .gwd-promo-plan {
	width: 460px !important;
}

#gwd_mini_compare_detail {
	left: 181px !important;
}
}
