@-moz-document domain("solotodo.com") {
html, body, .dashboard_text_comment {
		background: #1b1b1b !important;
		color: #eee !important;
	}
	.bg_border, .not_ahora {
		background: #1b1b1b !important;
	}
	.css-10nd86i {
		color:black;
	}
	.products-reel__item a {
		color: white
	}
	.short-description-product {
		background-color: #3a3a3d;
		height: 100%;
		width: 100%;
	}
	.products-reel .short-description-product {
		padding: 20px 15px 7px;
		border: 1px solid #33333361;
		border-radius: 3px;
	}
	.price-container {
		color: #ff1919;
	}
	.content-card {
		background-color: #1b1b1b;
		border-radius: 0px;
		/*! padding: 15px;
		*/
		/*! border: 1px solid #ddd;
		*/
		border: 3px solid #31303026;
	}
	.table {
