css:
@-moz-document domain(myshows.me) {
header {
    border-bottom: solid #6b5a79 3px;
    color: #6b5a79;
    background: #262729;
}
.content {
    color: #cccccc;
    background: #525355;
}
.showHeaderName, .showHeaderMeta {
    background: #525355;
}
nav:before {
    background: #555a65;
}
nav ul a {
    border-bottom: 1px solid #383838;
    background-color: #555a65;
}
[class^="col"]._borderLeft:before {
    border-left: 1px solid #383838;
}
.seasonBlockHeader:hover {
    border-color: #707373e8;
}
.seasonBlockHeader {
    border-bottom: 1px solid #676a6a87;
}
.seasonBlockBody {
    border-bottom: 1px solid #5c5f60;
}
.showHeader {
    border-bottom: 1px dotted #95adc6;
}
a {
    color: #abbfc5;
}
h3 {
    color: #383838;
}
.cFirm {
    color: #8dc8da !important;
}
.headerSearch input {
    background-color: #4e5256;
}
.seasonBlockBody table tr:nth-child(even) {
    background: #575c61;
}
a.linkBlock:hover .link {
    color: #cbccd4;
}
.indexTextImage + p {
    color: #b5b4b7;
}
a.button {
    color: #ccc;
    background-color: #6b5a79;
}
.catalogTable th {
    border-bottom: 3px solid #443d2f;
}
a.button._light {
    color: #999;
    background: #cccccc;
}
.infoList li:nth-child(even) {
    background: #595c5f;
}
.linkPseudo > span {
    border-bottom: 1px dashed #a8cccc;
    color: #a8cccc;
}
.bcFirmB {
    background-color: #493256 !important;
}
.commBlock {
    border-bottom: 1px solid #676a6d;
}
.headerSearch input:focus + button {
  background-color: #6b5a79;
}
._actions._sticking {
    background-image: linear-gradient(to right, rgba(255,255,255,0) 0px, #525355 255px, #525355 100%);
}
._actions._sticking:after {
    background-image: url(https://www.notning.url/none_324230-4923-09423-4-023.jpg);
}
button._dark {
    color: #e2fff4;
    background: #493256;
}
hr._fade {
    border-color: #383838;
}
.commSign {
    color: #fff;
    background: #3d3f40;
}
.commSign:before {
    border-top-color: #3d3f40;
    border-right-color: #3d3f40;
}
.commBlock._hint {
    background: #525355;
}
textarea {
    display: inline-block;
    width: 100%;
    padding: 0.25em 0.5em;
    vertical-align: baseline;
    border: 1px solid #3f4248;
    background-color: #717375;
}
hr {
    border-top: 1px solid #383838;
}
.paginator li {
    color: #999;
    background: #4e5256;
}
.paginator li > a {
    color: #999999;
}
.paginator li._active {
    color: #fff;
    background: #4a2a65;
}
.catalogTable th, .catalogTable td {
    border-bottom: 1px solid #727f82;
}
button {
    color: #e2fff4;
    background-color: #493256;
}
.searchSuggetions {
    color: #252525;
    background: #4e5256;
}
a,
.link {
  color: #899caf;
}
.presentBlockButtons a {
    border: solid #656867;
    border-width: 1px 0 0 1px;
}
.tabs_head ul li > a {
    color: #999;
    background: #cccccc;
}
.tabs_head ul {
    border-bottom: 1px solid #383838;
}
select {
    border: 1px solid #ccc;
    background-color: #ccc;
}
h1, h2, h4, h5, h6 {
    color: #ccc;
}
h3 {
    color: #a3b5ae;
}
.showHeaderMeta {
    color: #a2a2a2;
}
.cText {
    color: #a098bb !important;
}
.headerSearch._content input {
    border: 1px solid #472e5d;
    border-right: 0;
}
.headerSearch button {
    overflow: hidden;
    position: absolute;
    right: 0;
    top: 0;
    width: 30px;
    text-indent: -99999px;
    background: #5f2e88 url(https://myshows.me/shared/img/fe/icon_search.png) no-repeat 50% 50%;
    border-top-left-radius: 0;
    border-bottom-left-radius: 0;
}
input[type="text"] {
    border: 1px solid #ccc;
    background-color: #ccc;
}
footer .container {
    background: #232325;
}
._platoformForView._new {
    border: 1px solid #2b363a94;
    color: #cccccca6;
    background-color: #595c5f66;
}
.headerUser > div > ul > li > a:hover {
    background: #262729;
}
.plateHint {
    padding: 2rem;
    background-color: #464c5a;
}
.firmList li:before {
    color: #bb67ff;
}
.linkPseudo.cFirm > span {
    border-color: #bb67ff;
}
.seasonBlockHeader:hover:after {
  border-color: #bb67ff;
}
nav .navAlert {
    position: absolute;
    bottom: 33px;
    right: 22px;
    padding: 0 3px;
    height: 16px;
    line-height: 16px;
    font-size: 9px;
    font-weight: bold;
    color: #fff;
    background: #bb67ff;
}
nav .navAlert:before {
  content: "";
  position: absolute;
  top: 0;
  right: 100%;
  width: 0;
  height: 0;
  border: 2px solid transparent;
  border-top-color: #bb67ff;
  border-right-color: #bb67ff;
}
.labelPro {
    color: #fff;
    background: #5f2e88;
}
.tabs._buttons .tabs_head ul li._active > a {
    background: #5f2e88;
    color: #fff;
}
.tabs._buttons .tabs_head ul li > a {
    color: #78a5d4;
    background: transparent;
    padding: 0.25em 0.5em;
    min-width: 0;
    border-radius: 0;
}
.status._new {
    width: 14px;
    background-color: #8a05f9;
    background-position: 0 -40px;
}
.plateNote {
    padding: 1rem;
    background-color: #484b4e;
}
table.firmTable tr:nth-child(even),
.seasonBlockBody table tr:nth-child(even) {
  background: #555a6599;
}
.presentBlockBookmark._active {
    border-color: #5f2e88;
}
.mediaBlock ._bookmark._active {
    border-color: #5f2e88;
}
.statusBlock > b {
    background: #793eab;
}
.statusBlock {
    color: #fff;
    background: #b474ea;
}
.showProgress ._line {
    background: #793eab;
}
.showProgress ._done {
    color: #b57de2;
}
.calendar ._head {
    border-bottom: 1px solid #61646b;
    color: #8d94a5
}
.calendar {
    border-bottom: 1px solid #61646b;
}
.gallery._calendar .galleryControlLeft:before,
.gallery._calendar .galleryControlRight:before {
  width: 5px;
  height: 5px;
  border-width: 0 2px 2px 0;
  border-color: #8d94a5;
}
.gallery._calendar .galleryControlLeft:hover:before,
.gallery._calendar .galleryControlRight:hover:before {
  border-color: #b366f3;
}
.calendar ._week ._d {
    font-weight: bold;
    color: #2a2d33;
}
.mediaBlock._withSideInfo ._onImg {
    color: #bbb;
    background: #5f2e88;
}
.tabs_head ul li._active > a {
    color: #ccc;
    background: #525355;
}
.tabs_head ul li._active {
    border-bottom-color: #525355;
}
.tabs_head ul li {
    float: left;
    margin: 0 0 -1px;
    border: solid #383838;
    border-width: 1px 1px 1px 0;
}
.tabs_head ul li > a {
    color: #999;
    background: #4c4e50;
}
.statBlock ._info span {
    background: #6c6f75;
}
.statBlock ._info span._type1 {
    background: #c386f7;
}
.statBlock ._info span._type2 {
    background: #9833ef;
}
.statBlock ._info span._type3 {
    background: #541190;
}
.statBlock ._info span._type4 {
    background: #29054a;
}
.statBlock ._days td > div {
    background: #6c6f75;
}
.statBlock ._days td > div._type1 {
    background: #c386f7;
}
.statBlock ._days td > div._type2 {
    background: #9833ef;
}
.statBlock ._days td > div._type3 {
    background: #541190;
}
.statBlock ._days td > div._type4 {
    background: #29054a;
}





}


@-moz-document domain(myshows.me) {

body {
background-image: url(https://zzz.zzz/zzz.jpq) !important;
background: #242628;
}
header a > img {
	height: 0 !important;
	width: 0 !important;
	padding-left: 125px !important;
	padding-top: 25px !important;
	background: url(https://i.imgur.com/nDRRy98.png) no-repeat !important;
}
nav ul a:before {
  content: '';
  display: block;
  margin: 0 auto 6px;
  width: 54px;
  height: 38px;
  transition: 0.25s;
  background: url(https://i.imgur.com/lBdboQn.png) no-repeat 0 -88px;
}
.stars {
    display: inline-block;
    width: 89px;
    height: 17px;
    vertical-align: -0.2em;
    background: url(https://i.imgur.com/Zvjz5VF.png) no-repeat -90px 0;
}
.elementRate._compact > * {
    width: 21px;
    height: 21px;
    background: url(https://i.imgur.com/62IK5OY.png) no-repeat -37px -37px;
}
.elementRate > * {
    float: right;
    width: 24px;
    height: 24px;
    background: url(https://i.imgur.com/Z0MNQzw.png) no-repeat -37px -37px;
    transition: 0.25s;
}
.elementRate > *:nth-child(0n+6) {
    width: 30px;
    background-position: 2px -38px;
}
.iconEyeD {
    display: inline-block;
    width: 24px;
    height: 13px;
    background: url(https://i.imgur.com/AqNnFxO.png) no-repeat 0 0;
    filter: brightness(0.5);
}
.status {
    display: inline-block;
    width: 20px;
    height: 7px;
    background: #390 url(https://i.imgur.com/fQ47JGf.png) no-repeat 0 0;
    cursor: help;
}

}

@-moz-document domain(myshows.me) {
.seasonBlockHeader b {
  	font-weight: normal;
    font-size: 16px;
    color: #ababab;
	padding-left: 25px;
	padding-right: 20px;

}
.seasonBlockHeader {
	line-height: 30px;
}
.seasonBlockBody {
  margin-left: 60px;
}
.seasonBlockHeader:hover {
    border-color: transparent;
    background: #505256;
    
}

}
