css:
@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/"), url-prefix("https://tumblr.com/"), url-prefix("http://tumblr.com/") {
.identity{background-color:#16161a}
.ui_dialog_lock{background:#16161a}
.l-header-container--refresh{background-color:#16161a}
.l-container.l-container--two-column-dashboard .l-content,.l-container.l-container--two-column .l-content{background-color:#16161a}
.l-container.l-container--two-column-dashboard .right_column,.l-container.l-container--two-column .right_column{background-color:#16161a}
.l-container.l-container--two-column-dashboard .left_column{background-color:#16161a}
.plus-follow-button{color:#16161a}
.post_avatar{background-color:#16161a}
.identity .controls_section.user_list li .follow_list_item_blog:before{background-image:linear-gradient(90deg,rgba(35,35,37,0),#16161a);border-right:5px solid #16161a}
.discover_actions_discover .elevator,.search_actions_search .elevator{background-color:#00b8ff}
.post-forms-glass{background-color:rgba(35,35,37,.95)}
.blue#glass_overlay,.blue#hello_glass,.blue.glass{background-color:rgba(35,35,37,.95)}
.identity .right_column:after{background:linear-gradient(180deg,#16161a,rgba(35,35,37,0))}
.notifications_page .section.divider{background:transparent url(data:)}
.l-container.l-container--flex .l-content{background-color:#16161a}
.post_avatar .post_avatar_link{background-color:#16161a}
.search_results_container .tumblelog_mask_item{background-color:#16161a}
.radar .radar_footer .radar_avatar::before{background-image:linear-gradient(to right,rgba(35,35,37,0),#16161a)}
.notification,.notification *,.toastr .toast,#toaster .toast.toast_image_text_image .toast_inner .txt a,#toaster .toast.toast_image_text_image .toast_inner .txt,#toaster .toast.toast_image_text_icon .toast_inner .txt,.notification .notification_sentence .username,.notification .notification_sentence .banner_text,.notification .notification_sentence a,.subheading,#toaster .toast .toast_inner *{background-color:#16161a}
.post_full .post_permalink{border-top-color:#16161a;border-right-color:#16161a}
.rapid-recs-container .rapid-recs{background-image:linear-gradient(180deg,rgba(35,35,37,0) 0,#16161a 15%,#16161a 85%,rgba(35,35,37,0))}
.identity .controls_section.user_list li:active .follow_list_item_blog:before,.identity .controls_section.user_list li:hover .follow_list_item_blog:before{background-image:linear-gradient(90deg,rgba(18,18,18,0),#121212);border-right:5px solid transparent}
.compose-button{fill:#fb856d}
.reblog_follow_button .follow-text,.worded-follow-button .follow-text{color:#c2e1db}
.identity .controls_section .item:hover .follow:hover{background:#c2e1db}
.ui_peepr_glass{background-color:rgba(35,35,37,.95)}
.tab_notice .tab_notice_value{color:#16161a}
.tab-bar-container .tab_notice{color:#16161a}
.tab-notice--outlined{border-color:#16161a}
.dashboard_options_form,.tag_psa{border:1px solid #1f1f1f}
.sidebar_link.explore_link{color:#c2e1db}
.tab_notice{background:#c2e1db!important}
.binary_switch input[type="checkbox"]:checked ~ .binary_switch_track{background:#fb856d}
.chrome.blue{border-color:#fb856d;background:#fb856d}
.ui_dialog .chrome.blue{background-color:#fb856d}
}
