css:
@-moz-document domain(soundcloud.com) {
.sound__soundActions,
.sc-background-light,
.sc-button,
.playControls__bg,
.playControls__inner,
.modal.modalWhiteout,
.playControls__queue,
.queue.m-visible,
.queue__itemWrapper,
commentForm,
.commentForm__transition,
.m-small,
.visible {
	background-color: black;
	border-color: green;
}
html,
.sc-border-box,
body,
.l-container,
.l-content,
.dropbar__content,
.searchTitle {
	background-color: black;
	color: #ff8c00;
	border: none;
}
a.sc-link-dark,
a:hover {
	color: green;
}
.g-tabs-link,
.g-tabs-link:visited {
	color: #b701ff;
}
.g-dark .sc-type-light,
.g-dark a {
	color: #1cd2db;
}
.infoStats__title,
.soundTitle__usernameText,
.profileHeaderInfo__userName {
	color: #f00;
}
.infoStats__value,
.actorUser__username,
.profileHeaderInfo__additional {
	color: #ff0;
}
.queueItemView:hover,
.queueItemView.m-active:hover {
	background-color: #1a1b3c;
}
.queueItemView.m-active {
	background-color: #061a09;
}
.queueItemView__username a {
	color: #f00;
}
input,
select,
textarea {
	filter: invert();
}
.commentForm__wrapper,
.sc-button-follow .sc-button-selected {
	filter: invert();
	background-color: white;
	border-color: #00ff84;
}
.soundBadge__additional {
	background: linear-gradient(90deg, hsla(0, 0%, 0%, .1), #000 17px);
}
.sharePanel,.moreActions,
canvas {
	filter: invert(100%);
}
.sharePanel .sc-input,.sc-button-disabled, .sc-button-disabled:focus, .sc-button-disabled:hover, .sc-button.sc-pending, .sc-button.sc-pending:focus, .sc-button.sc-pending:hover, .sc-button:disabled, .sc-button:disabled:hover {
	background-color: black;
	border-color: green;
}
}
