css:
@-moz-document domain("animemusicquiz.com") {
:root { 
}

/*Backgronds*/
#gameChatPage > .col-xs-9 {
    background-image: url(https://tenor.com/view/yes-nice-cool-awesome-yeah-gif-12995040.gif);
    background-size: cover !important;}
#gameContainer {
    background-image: url(https://tenor.com/view/yes-nice-cool-awesome-yeah-gif-12995040.gif);
    background-size: cover !important;}
#startPage {
    background-image: url(https://tenor.com/view/yes-nice-cool-awesome-yeah-gif-12995040.gif);
    background-size: cover !important;}
#awMainView {
    background-image: url(https://tenor.com/view/yes-nice-cool-awesome-yeah-gif-12995040.gif);
    background-size: cover !important;}
#loadingScreen {
    background-image: url(https://tenor.com/view/yes-nice-cool-awesome-yeah-gif-12995040.gif);
    background-size: cover !important;}

/*Modal Boxes / pop-ups*/
:focus {
    outline-color: #ba3535;
}
.form-control:focus {
    border-color: #930066;
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(138,21,21,.6);
}
.customCheckbox label {
    color: #ba3535;
}
.swal2-popup .swal2-styled.swal2-confirm {
    background-color: #6a2020;
    border-color: rgb(140, 10, 140);
}
.swal2-popup .swal2-styled:focus {
    box-shadow: 0 0 0 2px #fff, 0 0 0 4px rgba(75,20,75,.5)
}

/*Link colors - EVERYWHERE*/
a:hover {
    text-decoration: bold underline solid currentcolor !important;
}
a:link {
    color: #ac55ac;
}
a:visited {
    color: #ba3535;
}

/* These are for all of the links on the login screen.
Keep these specifically no matter the state because it looks better*/
.loginMainForm a {
    color: #ac55ac;
}

/*Coloration of the friends marker in game selection*/
.fa-friend {
    color: #cc4a22;
}

/*The color of the indicator for the active promo banner*/
.mpPromoCycleEntry.active {
    background-color: #cc4a22;
    box-shadow: 0 0 10px 2px #ee6c66;
}

/*In-game colors*/
/*The little mark to the right of the host's name*/
.avatarIsHostContainer {
    background-color: #8a1515;
}
/*The corner of the answer pop-up before result checking*/
.unknownResult {
    background-color: #aa4433;
}
/*Lobby styling*/
.lbReady .lobbyAvatarImgContainer {
	-webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
	box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
}
.lbReady .lobbyAvatarTextContainer {
	-webkit-box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
	box-shadow: 0px 0px 10px 2px rgba(150, 15, 0, 0.65);
}
/*This section changes the particle look for the flying notes/xp when guessing correctly*/
/*The the glow in and around the given answer*/
.particleEmiter {
    -webkit-box-shadow: 0 0 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
    box-shadow: 0 0 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
    -webkit-text-shadow: 10 10 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
    text-shadow: 10 10 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
}
/*The particles you see flying to your xp-bar*/
.particle {
    background-color: #ac55ac;
    -webkit-box-shadow: 0 0 5px #ac55ac, 0 0 10px #ac55ac, 0 0 15px #ac55ac;
    box-shadow: 0 0 5px #ac55ac, 0 0 10px #ac55ac, 0 0 15px #ac55ac;
}
/*The glow around the particles*/
.particleShadow {
    -webkit-box-shadow: 0 0 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
    box-shadow: 0 0 10px #ac55ac, 0 0 20px #ac55ac, 0 0 30px #ac55ac, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
}

/*The vote skip button after voting skip*/
#qpVoteSkip.toggled {
    background-color: #5a1515;
}

/*This is for the answer pop-up, when you either get points or lose hope*/
/*When the answer is wrong, go red*/
.qpAvatarAnswerContainer.wrongAnswer .qpAvatarAnswer {
    text-shadow: 10 10 10px #8a1515, 0 0 20px #8a1515, 0 0 30px #8a1515, 0 0 40px #5a1515, 0 0 70px #5a1515, 0 0 80px #5a1515, 0 0 100px #5a1515, 0 0 150px #5a1515;
}
.knownResult.wrong {
    background-color: #5a1515;
    -webkit-box-shadow: 0 0 1px 2px #8a1515, 0 0 2px 2px #8a1515, 0 0 7px 2px #5a1515, 0 0 10px 2px #5a1515, 0 0 15px 2px #5a1515, 0 0 20px 2px #5a1515, 0 0 40px 2px #5a1515;
    box-shadow: 0 0 1px 2px #8a1515, 0 0 2px 2px #8a1515, 0 0 7px 2px #5a1515, 0 0 10px 2px #5a1515, 0 0 15px 2px #5a1515, 0 0 20px 2px #5a1515, 0 0 40px 2px #5a1515;
}
/*When the answer is right, go purple. Sticking to warm tones*/
.knownResult.correct {
    background-color: #ac55ac;
    -webkit-box-shadow: 0 0 1px 2px #aa55dd, 0 0 2px 2px #aa55dd, 0 0 7px 2px #ac55ac, 0 0 10px 2px #ac55ac, 0 0 15px 2px #ac55ac, 0 0 20px 2px #ac55ac, 0 0 40px 2px #ac55ac;
    box-shadow: 0 0 1px 2px #aa55dd, 0 0 2px 2px #aa55dd, 0 0 7px 2px #ac55ac, 0 0 10px 2px #ac55ac, 0 0 15px 2px #ac55ac, 0 0 20px 2px #ac55ac, 0 0 40px 2px #ac55ac;
}
.qpAvatarAnswerContainer.rightAnswer .qpAvatarAnswer {
    text-shadow: 10 10 10px #aa55dd, 0 0 20px #aa55dd, 0 0 30px #aa55dd, 0 0 40px #ac55ac, 0 0 70px #ac55ac, 0 0 80px #ac55ac, 0 0 100px #ac55ac, 0 0 150px #ac55ac;
}
/*The progress bar below the video during results*/
#qpVideoProgressBar {
    background-color: rgba(150,0,25,0.4);
}
/*The glow around the part where you enter the answer*/
#qpAnswerInputContainer.focused {
    box-shadow: 0 0 15px 1px #cc2525;
}
/*This is for the auto-complete and the drop-down*/
/*The text matching your entry*/
.awesomplete mark {
    color: #000000;
    background-color: #523252;
    padding: 0;
}
/*Generic list colors*/
.awesomplete > ul {
    background: #424242;
    border: none;
    -webkit-box-shadow: 0 0 10px 2px rgb(0, 0, 0);
    box-shadow: 0 0 10px 2px rgb(0, 0, 0);
}
/*The selected entry from the drop-down*/
.awesomplete > ul > li[aria-selected="true"] {
    background-color: #330033;
}
/*The matching text in the selected entry*/
.awesomplete > ul > li[aria-selected="true"] mark {
    color: hsl(0, 81%, 44%);
    background-color: #660066;
}
/*The entry your mouse hovers over*/
.awesomplete > ul >  li:hover {
    background-color: #663366;
}
/*The matching text on the hovered entry*/
.awesomplete > ul > li:hover mark {
    background-color: #660066;
}
/*The glow from the Vote Skip button*/
#qpVoteSkipGlowContainer {
    box-shadow: 3px 0px 10px 5px #cc2525;
}

/*Floating containers*/
.floatingContainer {
    background-color: rgba(35,35,35,0.4);
    border-color: rgba(10,10,10,0.5);
}

#footerMenuBar .menuBar, #optionsContainer.popOut, #rightMenuBarPartContainer::before, .gcList > li:nth-child(2n) {
    background-color: #1b1b1b
}

#socialTab:not(.open), #optionsContainer:not(.open) {
    display:none;
}

#mainMenuSocailButton, #avatarUserImgContainer {
    border:none !important;
}

#optionsContainer li {
    background-color:#424242 !important;
}

#rbMajorFilters {
    background-color: rgb(0, 0, 0);
    padding-left: 10px;
}


.leftShadowBorder {
	box-shadow: none;
	-webkit-box-shadow: none;
}

.gcList > li:nth-child(2n) {
background: none;
}

.gcInputContainer {
background: none;
}

/* Legibility fix for guesses */
.qpAvatarAnswerContainer.rightAnswer .qpAvatarAnswer {
    box-shadow: none;
	text-shadow: 0 0 20px #96FF96, 0 0 30px #66FF66, 0 0 40px #66FF66, 0 0 50px #66FF66 !important;
}
.qpAvatarAnswerContainer.wrongAnswer .qpAvatarAnswer {
    box-shadow: none;
	text-shadow: 0 0 20px #FF9996, 0 0 30px #FF4A44, 0 0 40px #FF4A44, 0 0 50px #FF4A44 !important;
}

.avatarIsHostContainer {
	box-shadow: none;
    -webkit-box-shadow: none;
}

.avatarIsHostContainerOuter {
 	position: initial;
	box-shadow: none;
    -webkit-box-shadow: none;
}

.qpAvatarNameContainer .avatarIsHostContainer {
    background-color: unset;
	background-image: url("https://files.catbox.moe/jizubh.png");
    transform: none;
    left: 25px;
    width: 20px;
    height: 20px;
    margin-top: 4px;
}
#mainMenuSocailButton, .lobbyAvatarImgContainer, .menuBarOptionContainer { 
	background-color: rgba(0,0,0,0.7);
}


#avatarUserImgContainer {
  opacity: 0.5;
}

#avatarUserImgContainer:hover {
  opacity: 1.0;
}
.lobbyAvatarTextContainer {
    background: rgba(0,0,0,0.0);
}

#qpAnimeNameHider , #qpVideoHider, #qpInfoHider, .qpAvatarNameContainer, .qpAvatarAnswerContainer, .qpVideosUserHidden,.qpVideoOverlay, #qpVoteSkip, #qpVoteState, #qpWaitBuffering  {
	background-color: rgb(0, 0, 0);
}

#qpUpvoteContainer.selected {
	background-color: #5a1515;
}
#qpReportContainer.selected {
	background-color: #74204d;
}
#qpDownvoteContainer.selected {
	background-color: #ac55ac;
}
.qpLifeActiveOverlay {
	background-color: #B6FF00;
	-webkit-box-shadow: inset 0 0 5px #fff, inset 0 0 10px #fff, 0 0 3px #fff, 0 0 5px #fff, 0 0 15px #5a1515;
	box-shadow: inset 0 0 5px #fff, inset 0 0 10px #fff, 0 0 3px #fff, 0 0 5px #fff, 0 0 15px #5a1515;
}
/*Life stuff for LMS/BR*/
.qpLifeChargeOverlay {
	background-color: #8a1515;
	box-shadow: inset 0 0 2px #ffffff80, inset 0 0 5px #ffffff80, 0 0 3px #ffffff80, 0 0 7px #8a1515;
	box-shadow: inset 0 0 2px #ffffff80, inset 0 0 5px #ffffff80, 0 0 3px #ffffff80, 0 0 7px #8a1515;
}
.qpLife.target {
	background-color: #5a1515;
}
#brMap:focus-within {
	-webkit-box-shadow: 0 0 5px rgba(205, 35, 35, 0.6);
	box-shadow: 0 0 5px rgba(205, 35, 35, 0.6);
}
.brTile > div {
    background-color: #8a1515;
}
.brMapObject.active {
	-webkit-box-shadow: 0 0 5px rgba(205, 35, 35, 0.6);
	box-shadow: 0 0 5px rgba(205, 35, 35, 0.6);
	border-color: #8a1515;
}
.brMapObject.active.inventoryFull {
	border-color: #ac55ac;
}
.brPlayer.self > .brPlayerImage {
	-webkit-box-shadow: 0px 0px 15px 4px rgb(138, 21, 21);
	box-shadow: 0px 0px 15px 4px rgb(138, 21, 21);
}
#brCollectionContainer.lowSlotsCount >#brCollectedList > li::after {
	background-image: -webkit-linear-gradient(to left, rgb(0, 0, 0), rgba(0, 0, 0, 0));
	background-image: -webkit-linear-gradient(to left, rgb(0, 0, 0), rgba(0, 0, 0, 1));
	background-image: -webkit-linear-gradient(to left, rgb(0, 0, 0), rgba(0, 0, 0, 2));
	background-image: -webkit-linear-gradient(to left, rgb(0, 0, 0), rgba(0, 0, 0, 3));
    background-image: linear-gradient(to left, rgb(0, 0, 0), rgba(0, 0, 0, 0));
}



#gcGameChatButton {
    text-shadow: #000000;

}
#gcChatMenu > div.selected , .tab-modal .tab.selected {
    background: none;
    background-color:rgb(40, 40, 40) !important;
}

#elQuestionNoSongSelectedContainer, .modal-content, .tab-modal .tabContainer, .tab-modal .modal-header, .topMenuButton, .topMenuBar > .topMenuBack,  .topRightBackButton{
    background-color:rgba(0, 0, 0);
}


 .qpAvatarImgContainer {
    box-shadow: 0 0 0px 0px rgb(0, 0, 0);
    display: inline-block;
    background-color: rgba(0,0,0,0.6);
    background-size: cover;
    position: relative;
    z-index: -1;
}


.qpAvatarLevelText {
   background-color: rgb(0, 0, 0);
    border-radius: 1px 1px 50px 50px 
}

 .qpAvatarPointText {
   background-color: rgb(0, 0, 0);
    border-radius: 50px 50px 1px 1px 
} 


.leftShadowBorder {
	box-shadow: none;
	-webkit-box-shadow: none;
}

.gcList > li:nth-child(2n) {
background: none;
}

.gcInputContainer {
background: none;
}


#xpBackground{
    	background: #af00ff;
    	border: #d53cf9;
}

