css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("64digits.com") {
/* Top bar */
.top_main_color_bar_off a {color: #737373!important;}
.top_main_color_bar_on a {color: #972626!important;}
.top_main_color_bar_on:active {background-color: #000000!important;}

/* Side menu links */
a.no_underline {color: #7D7D7D!important;}
a.no_underline:hover {color: #972626!important;}
a.no_underline:active {color: #FFFFFF!important;}

/* Buttons */
.ms_button {color: #7D7D7D!important; font-size: 10px!important;}

/* Side boxes */
.left_menu_bg {background-color: #E7E7E7!important; color: #7D7D7D!important; font-family: "Verdana"!important; font-size: 10px!important;}

/* Side box titles */
.left_login_box .ms_font {background-color: #972626!important; color: #FFFFFF!important; font-family: "Verdana"!important; font-size: 10px!important;}

/* Blog box */
.left_blog_cell_off {background-color: #E7E7E7!important; font-family: "Verdana"!important; font-size: 10px!important;}
.left_blog_cell_on {background-color: #EFEFEF!important; font-family: "Verdana"!important; font-size: 10px!important;}

/* Quote Box */
.quote_header {background-color: #779977!important; color: #FFFFFF!important; border: 1px solid #FFFFFF!important; border-bottom: 1px dashed #FFFFFF!important; text-align: center!important; font-family: "Verdana"!important;}
.quote {background-color: #557755!important; border: 1px solid #FFFFFF!important; border-top: 0px solid #FFFFFF!important; font-style: italic!important; color: #FFFFFF!important; font-family: "Verdana"!important; font-size: 12px!important;}

/* Code Box */
.code_header {background-color: #8899AA!important; color: #FFFFFF!important; border: 1px solid #FFFFFF!important; border-bottom: 1px dashed #FFFFFF!important; text-align: center!important;}
.code {background-color: #556677!important; color: #FFFFFF!important; border: 1px solid #FFFFFF!important; border-top: 0px solid #FFFFFF!important;}
}
