css:
@-moz-document domain("op.gg") {
.l-container {
	background-color: #0a0a0a;
	color: white;
}
.SummonerLayout>.Menu>.MenuList>.Item.inGame {
	position: relative;
	margin-left: 15px !important;
	background-color: #0a0a0a;
	color: white;
}
.SummonerLayout>.Menu {
	border-bottom: 1px solid #0a0a0a;
	margin-top: 25px;
	background-color: #0a0a0a;
	color: white;
}
.SummonerLayout>.Menu>.MenuList>.Item {
	display: inline-block;
	margin: 0 -1px 0 0;
	padding: 0;
	background-color: #0a0a0a;
	color: white;
}
.SummonerLayout>.Menu>.MenuList>.Item.active>a {
	border-bottom: 0px;
	background-color: #0a0a0a;
	color: white;
}
.SummonerLayout>.Menu>.MenuList>.Item>a {
	border-bottom: 0px;
	background-color: #0a0a0a;
	color: white;
}
.SummonerLayout>.Menu>.MenuList>.Item>a:hover {
	border-bottom: 0px;
	background-color: #0a0a0a;
	color: white;
}
.SummonerLayout>.Header>.PastRank {
	margin-top: -5px;
	padding-left: 30px;
	padding-bottom: 15px;
	background-color: #0a0a0a;
	color: white;
}
.SummonerLayout>.Header>.PastRank>.PastRankList {
	display: inline-block;
	margin: 0 0 0 5px;
	padding: 0;
	background-color: #0a0a0a;
	color: white;
}
.SummonerLayout>.Header>.PastRank>.PastRankList>.Item {
	display: inline-block;
	padding: 3px 4px 2px 4px;
	margin-right: 3px;
	background-color: #0a0a0a;
	color: white;
	font-size: 11px;
	border: 0px;
	border-radius: 3px;
}
.SummonerLayout>.Header>.Profile {
	position: relative;
	display: inline-block;
	height: 100px;
	margin: 0 25px 0 25px;
	vertical-align: top;
	line-height: 1.1;
	background-color: #0a0a0a;
	color: white;
}
.SummonerLayout>.Header>.Profile>.Information>.Name {
	color: white;
	font-size: 20px;
	font-weight: bold;
	margin-right: 4px;
	vertical-align: middle;
}
.SummonerLayout>.Header>.Profile>.Information>.FavoriteButton {
	margin-left: 4px;
	vertical-align: 1px;
	text-decoration: none;
	color: white;
	display: inline-block;
	border: 0px;
	border-radius: 3px;
	padding: 3px 5px;
	font-size: 10px;
}
.SummonerLayout>.Header>.Profile>.Information>.Rank>.LadderRank>.Link {
	color: white;
	font-size: 11px;
	text-decoration: none;
	line-height: 1.5;
}
.SummonerRatingMedium {
	background-color: #0a0a0a;
	color: white;
}
.SummonerRatingMedium>.TierRankInfo>.RankType {
	font-size: 12px;
	color: white;
}
.SummonerRatingMedium>.TierRankInfo>.TierInfo>.LeaguePoints {
	color: white;
	font-weight: bold;
}
.wins {
	color:lightgreen;
	font-weight: bold;
}
.losses {
	color:red;
	font-weight: bold;
}
.Box {
	border: 1px solid #0a0a0a;
	box-shadow: 0 1px #0a0a0a;
	background-color:#0a0a0a;
	border-radius: 2px;
}
.sub-tier {
	margin-top: 8px;
	padding: 16px 0;
	display: flex;
	display: -ms-flexbox;
	align-items: center;
	background-color: #0a0a0a;
	border: solid 1px #0a0a0a;
}
.sub-tier__rank-tier.unranked {
	color: #1f8ecd;
	font-size: 15px;
}
.GameListContainer>.Content .GameItemList {
	margin-top: 0px;
}
.GameItemList>.GameItemWrap {
	position: relative;
	border-radius: 3px;
	margin-bottom: 1px;
}
.Box>.Tab>.TabList>.Item.active {
	border-bottom-color: #ededed;
	background-color: #0a0a0a;
	color: white;
	font-weight: bold;
	border-bottom: 0;
}
.Box>.Tab>.TabList>.Item {
	display: table-cell;
	border-left: 1px solid #0a0a0a;
	border-bottom: 1px solid #0a0a0a;
	background-color: #0a0a0a;
	vertical-align: middle;
	cursor: pointer;
	color: white;
	text-align: center;
}
.GameItem.Lose>.Content {
	table-layout: fixed;
	background-color: #e09c98;
	border-color: #e09c98;
}
.GameItem>.Content {
	display: table;
	width: 689px;
	border-collapse: collapse;
	border: 0px;
}
.GameItem.Win>.Content {
	table-layout: fixed;
	background-color: #a3cfec;
	border-color: #a3cfec;
}
.GameItem.Win>.Content>.StatsButton {
	border-color: #64b1e4;
	background-color: #a3cfec;
}
.GameItem.Lose>.Content>.StatsButton {
	border-color: #d67b77;
	background-color: #e09c98;
}
.GameAverageStats {
	display: table;
	width: 100%;
	table-layout: auto;
	background-color: #0a0a0a;
}
.GameAverageStats tr>.Title {
	padding: 16px 0 14px 20px;
	line-height: 14px;
	font-size: 12px;
	color: white;
	height: 14px;
	border-left: 1px solid #0a0a0a;
}
.GameAverageStats tr>.Summary .WinRatioGraph .Text {
	position: absolute;
	left: 0;
	top: 50%;
	text-align: center;
	width: 100%;
	margin-top: -8px;
	line-height: 16px;
	font-size: 14px;
	color: white;
}
.GameAverageStats tr>.KDA>.KDA>.Kill {
	color: lightgreen;
}
.GameAverageStats tr>.KDA>.KDA>.Death {
	color: red;
}
.GameAverageStats tr>.KDA>.KDA>.Assist {
	color: yellow;
}
.GameAverageStats tr>.KDA>.KDARatio>.KDARatio {
	color: white;
}
.GameAverageStats tr>.KDA>.KDARatio>.CKRate {
	color: white;
}
.GameAverageStats tr>.Title:first-child {
	border-left: none;
	padding-left: 24px;
	text-align: center;
	font-size: 15px;
	font-weight: bold;
}
.GameAverageStats tr>.MostChampion ul li .WonLose {
	display: inline-block;
	font-size: 11px;
	line-height: 12px;
	color: white;
}
.GameAverageStats tr>.MostChampion ul li .Name {
	margin-bottom: 2px;
	line-height: 16px;
	font-size: 14px;
	color: white;
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
}
.GameAverageStats tr>.PositionStats {
	width: 183px;
	border-left: 0px;
	white-space: nowrap;
	vertical-align: middle;
}
.GameAverageStats tr>.MostChampion {
	width: 229px;
	border-left: 0px;
}
.SummonerLayout>.Menu>.MenuList>.Item>a {
	display: block;
	min-width: 70px;
	height: 39px;
	border: 0px;
	padding-left: 6px;
	padding-right: 6px;
	background-color: #0a0a0a;
	color: white;
	font-size: 13px;
	line-height: 40px;
	text-decoration: none;
	text-align: center;
}
.SummonerLayout>.Header>.LastUpdate {
	position: absolute;
	left: 160px;
	bottom: -18px;
	height: 12px;
	color: white;
	font-size: 11px;
}
.sub-tier__rank-type, .sub-tier__league-point, .sub-tier__gray-text, .sub-tier__gray-text {
	color: white;
}
.GameAverageStats tr>.MostChampion ul li .KDA.normal {
	display: inline-block;
	line-height: 12px;
	font-size: 11px;
	color: white;
	font-weight: bold;
}
.GameAverageStats tr>.PositionStats>ul>li .PositionStatContent .Name {
	line-height: 16px;
	font-size: 14px;
	color: white;
	margin-bottom: 4px;
}
.GameAverageStats tr>.PositionStats>ul>li .PositionStatContent .WinRatio {
	line-height: 12px;
	font-size: 11px;
	color: white;
}
.GameAverageStats tr>.PositionStats>ul>li .PositionStatContent .WinRatio span {
	color: white;
}
.Box>.Navigation .List>.Item>.Link {
	display: inline-block;
	color: white;
	font-size: 12px;
	line-height: 36px;
	text-decoration: none;
}
.Box>.Navigation .List>.Item>.Link:hover {
	color: white;
}
.jcf-select-SelectMatchTypes .jcf-select-text {
	display: inline;
	padding-right: 12px;
	color: white;
	font-size: 12px;
	line-height: 36px;
	text-overflow: ellipsis;
	white-space: nowrap;
	cursor: default;
	overflow: hidden;
}
element.style {
	display: block;
	background-color: #0a0a0a;
}
.ChampionBox {
	display: table;
	width: 100%;
	border-bottom: 0px;
	color: white;
	text-align: center;
}
.ChampionBox>.ChampionInfo>.ChampionName {
	width: 70px;
	color: white;
	font-weight: bold;
	font-size: 13px;
	text-overflow: ellipsis;
	overflow: hidden;
	white-space: nowrap;
}
.ChampionBox>.PersonalKDA>.KDAEach>.Kill {
	font-size: 12px;
	color: lightgreen;
	white-space: nowrap;
}
.ChampionBox>.PersonalKDA>.KDAEach>.Death {
	font-size: 12px;
	color: red;
	white-space: nowrap;
}
.ChampionBox>.PersonalKDA>.KDAEach>.Assist {
	font-size: 12px;
	color: yellow;
	white-space: nowrap;
}
.ChampionBox>.Face {
	display: table-cell;
	width: 60px;
	padding: 5px 0 0 0;
	text-align: right;
	vertical-align: middle;
	background-color: #0a0a0a;
}
.ChampionBox>.ChampionInfo {
	display: table-cell;
	width: 85px;
	text-align: left;
	vertical-align: middle;
	padding-left: 10px;
	background-color: #0a0a0a;
}
.ChampionBox>.PersonalKDA {
	display: table-cell;
	line-height: 1.8;
	vertical-align: middle;
	background-color: #0a0a0a;
}
.ChampionBox>.Played {
	display: table-cell;
	width: 70px;
	line-height: 1.8;
	vertical-align: middle;
	background-color: #0a0a0a;
}
.MostChampionContent>.MoreButton>.Action {
	display: block;
	text-decoration: none;
	padding: 11px 0 10px;
	line-height: 15px;
	font-size: 12px;
	text-align: center;
	color: white;
	background-color: #0a0a0a;
}
.SummonerLayout>.Menu>.MenuList>.Item.inGame>a {
	padding-left: 20px;
	padding-right: 20px;
	background-color: #0a0a0a;
	color: white;
}
.SummonerLayout>.Menu>.MenuList>.Item.inGame>a:hover {
	padding-left: 20px;
	padding-right: 20px;
	background-color: #0a0a0a;
	color: white;
}
.ChampionWinRatioBox>.Face {
	display: table-cell;
	width: 44px;
	padding: 5px 0 0 0;
	text-align: right;
	vertical-align: middle;
	background-color: #0a0a0a;
}
.ChampionWinRatioBox>.ChampionInfo {
	display: table-cell;
	width: 61px;
	text-align: left;
	vertical-align: middle;
	background-color: #0a0a0a;
}
.ChampionWinRatioBox>.WinRatio, .PositionWinRatioBox>.WinRatio {
	display: table-cell;
	width: 40px;
	vertical-align: middle;
	background-color: #0a0a0a;
	color:white;
}
.ChampionWinRatioBox>.RatioGraph, .PositionWinRatioBox>.RatioGraph {
	display: table-cell;
	height: 40px;
	vertical-align: middle;
	padding-left: 6px;
	padding-right: 12px;
	background-color: #0a0a0a;
}
.ChampionWinRatioBox>.ChampionInfo>.ChampionName {
	width: 90%;
	margin-left: 10px;
	color: white;
	font-weight: bold;
	text-overflow: ellipsis;
	white-space: nowrap;
	overflow: hidden;
	text-decoration: none;
}
.Box>.Title {
	position: relative;
	display: block;
	padding: 10px 15px;
	border-bottom: 0px;
	color: white;
	font-size: 12px;
	font-weight: bold;
	text-decoration: none;
}
.ChampionWinRatioBox, .PositionWinRatioBox {
	display: table;
	width: 100%;
	border-bottom: 0px;
	color: white;
	text-align: center;
	font-size: 12px;
	table-layout: fixed;
}
.SummonersMostGameTable>.Header>.Row>.HeaderCell.SummonerName {
	text-align: left;
	padding-left: 15px;
	background-color: #0a0a0a;
	color: white;
}
.SummonersMostGameTable>.Header>.Row>.HeaderCell {
	padding-top: 11px;
	padding-bottom: 10px;
	background-color: #0a0a0a;
	color: white;
	font-weight: normal;
	font-size: 12px;
	word-break: break-word;
}
.SummonersMostGameTable>.Body>.Row.Odd>.Cell {
	padding-top: 11px;
	padding-bottom: 10px;
	color: white;
	font-size: 11px;
	background-color: #0a0a0a;
}
.SummonersMostGameTable>.Body>.Row>.Cell {
	padding-top: 11px;
	padding-bottom: 10px;
	color: white;
	font-size: 11px;
	background-color: #0a0a0a;
}
.SummonersMostGameTable>.Body>.Row>.Cell.SummonerName>.Link {
	color: white;
	font-size: 12px;
	font-weight: bold;
	text-decoration: none;
}
.SummonersMostGameTable>.Body>.Row>.Cell.WinRatio {
	color: white;
}
.GameMoreButton>.Button {
	display: block;
	height: 50px;
	color: white;
	font-size: 13px;
	line-height: 50px;
	text-align: center;
	text-decoration: none;
}
.footer__links {
	line-height: 15px;
	font-size: 12px;
	color: white;
}
.footer__copyright {
	color: white;
}
.opscore-banner {
	margin-top: 8px;
	border: 0;
}
.l-menu {
	background: none;
	height: 48px;
	border-bottom: 0px;
	box-sizing: border-box;
	background-color: #050505;
}
.menu__item>a {
	color: white;
	display: flex;
	align-items: center;
	border-bottom: 3px solid transparent;
	padding: 13px 0;
}
.l-wrap .gnb .opgg-header__logo {
	position: absolute;
	left: 0;
	top: 0;
	width: 98px;
	background-color: #050505;
}
.l-wrap .gnb .opgg-header__navi {
	-webkit-flex: 1 1 auto;
	flex: 1 1 auto;
	display: flex;
	align-items: center;
	background-color: #050505;
}
.l-wrap .gnb .opgg-header__util {
	display: flex;
	align-items: center;
	background-color: #050505;
}
.l-wrap .gnb .opgg-header__gnb {
	display: -webkit-box;
	display: -webkit-flex;
	display: flex;
	-webkit-box-direction: normal;
	-webkit-box-orient: horizontal;
	-webkit-flex-direction: row;
	flex-direction: row;
	-webkit-flex-wrap: nowrap;
	flex-wrap: nowrap;
	-webkit-box-pack: justify;
	-webkit-justify-content: space-between;
	justify-content: space-between;
	-webkit-align-content: flex-start;
	align-content: flex-start;
	-webkit-box-align: start;
	-webkit-align-items: flex-start;
	align-items: flex-start;
	position: relative;
	box-sizing: border-box;
	height: 40px;
	padding-left: 98px;
	background-color: #050505;
}
.LeagueContainer>.LeagueContent .Table>.Content>.Row>.Cell {
	height: 42px;
	border-bottom: 0px;
	color: white;
	font-size: 13px;
	text-align: center;
}
.LeagueContainer>.LeagueContent .Table>.Content>.Row.series>.Cell {
	background-color: #0A0A0A;
}
.LeagueContainer>.LeagueContent .Table>.Header>.Row>.HeaderCell {
	height: 40px;
	border-bottom: 0px;
	color: #555e5e;
	font-size: 13px;
	text-align: center;
	font-weight: normal;
}
.LeagueContainer>.LeagueHeader>.LeagueInfo>.LeagueTierRank {
	color: white;
	font-size: 22px;
	font-weight: bold;
	margin-bottom: 12px;
}
.Button.Green {
	border: 0px;
	background: #36a4a4;
	color: white;
}
.ChampionStatsTable>.Header>.Row>.HeaderCell {
	height: 54px;
	border-bottom: 0px;
	color: white;
	font-size: 12px;
	text-align: center;
	font-weight: bold;
	background-color: #0A0A0A;
}
.ChampionStatsTable>.Body>.Row>.Cell {
	height: 52px;
	border-bottom: 0px;
	color: white;
	font-size: 12px;
	font-weight: bold;
	background-color: #0A0A0A;
}
.ChampionStatsTable>.Body>.Row.TopRanker>.Cell.Rank {
	color: white;
	background-color: #0A0A0A;
}
.ChampionStatsTable>.Body>.Row>.Cell.ChampionName>a {
	color: white;
	text-decoration: none;
}
.WinRatioGraph>.WinRatio {
	color: white;
	font-size: 12px;
	vertical-align: middle;
	margin-left: 6px;
}
.ChampionStatsTable>.Body>.Row>.Cell.KDA>.KDA>.Kill {
	color: lightgreen;
}
.ChampionStatsTable>.Body>.Row>.Cell.KDA>.KDA>.Death {
	color: red;
}
.ChampionStatsTable>.Body>.Row>.Cell.KDA>.KDA>.Assist {
	color: yellow;
}
.ChampionStatsTable>.Body>.Row>.Cell.Value {
	padding: 0 5px;
	color: white;
	text-align: center;
}
.Box>.Navigation {
	position: relative;
	border-bottom: 0px;
	margin-bottom: -1px;
	overflow: hidden;
	height: 34px;
}
.ErrorMessage {
	display: block;
	padding: 126px 0 60px;
	text-align: center;
	font-size: 16px;
	color: #555e5e;
	background-color: #0A0A0A;
}
html {
	background: #0A0A0A;
}
.Button.RealWhite {
	border: 0px;
	background: #0A0A0A;
	color: white;
}
.SpectateSummoner>.Box>.Title {
	position: relative;
	padding: 12px;
	color: white;
	font-size: 13px;
	text-align: left;
	vertical-align: middle;
}
.SpectateSummoner>.Box>.Title>small {
	margin-left: 8px;
	padding-left: 8px;
	border-left: 0px;
	font-size: 11px;
	color: white;
	font-weight: normal;
}
.SpectateSummoner>.Box>.Title>small.Time {
	color: white;
}
