	color: white;
	text-align: center;
	font-weight: normal;
}
.SpectateSummoner>.Box>.Content>.Table>.Body>.Row {
	height: 37px;
	background-color: #0A0A0A;
	color: white;
}
.SpectateSummoner>.Box>.Content>.Table>.Body>.Row:nth-child(even) {
	height: 37px;
	background-color: #0A0A0A;
	color: white;
}
.SpectateSummoner>.Box>.Content>.Table>.Body>.Row>.Cell.SummonerName>.SummonerName {
	display: inline-block;
	max-width: 100%;
	font-size: 12px;
	color: white;
