.SpectateSummoner>.Box>.Content>.Table.Team-200 {
	border-top: 0px;
}
.SpectateSummoner>.Box>.Content>.Table>.Body>.Row>.Cell.RuneMastery>.Button {
	width: 100%;
	padding: 8px 0;
	line-height: 14px;
	font-size: 12px;
	color: white;
	background-color: #0A0A0A;
}
.Button.White {
	border: 0px;
	background: #f2f2f2;
	color: #242929;
}
.SpectateSummoner>.Box>.Content>.Table>.Body>.Row>.Cell.BannedChampion {
	text-align: center;
	border-left: 0px;
	vertical-align: top;
	padding-top: 0;
}
.SpectateSummoner>.Box>.Content>.Table>.Body>.Row>.Cell.CurrentSeasonTierRank {
	font-size: 12px;
	color: white;
}
.SpectateSummoner>.Box>.Content>.Table>.Body>.Row>.Cell.RankedWinRatio {
	text-align: center;
	font-size: 11px;
	color: white;
	line-height: 13px;
}
.SpectateSummoner>.Box>.Content>.Table>.Body>.Row>.Cell.ChampionInfo {
	text-align: center;
	font-size: 11px;
	color: white;
	line-height: 16px;
}
