css:
@-moz-document url-prefix("http://heliohost.grd.net.pl/") {
    /***** body *****/
    body {
        background-color: #f1f1f1;
        font-family: 'Segoe UI', 'Roboto', arial, sans-serif;
    }

    #bodyc {
        width: 75%;
    }

    #bodycont {
        width: 85%;
    }



    /***** header *****/
    #header {
        width: 100%;
        background: #444;
        height: 80px;
        box-shadow: 0px 2px 3px 0px rgba(0, 0, 0, 0.3);

        position: absolute;
        transform: translateX(-50%);
        left: 50%;
        z-index: 1;
    }
    #headid {
        width: 100%;
        text-align: center;
    }
    #headid h1 {
        text-align: center;
        color: #eee;
        padding-top: 8px;
    }
    #headid p {
        text-align: center;
        color: gray !important;
    }
    #headid p span {
        color: gray !important;
    }
    #headid p::after {
        content: ' & styled by Kristoffer';
    }


    /***** mains *****/
    /* Various resources: HelioHost support forum */
    #mainl {
        display: none;
    }


    /* main */
    #mainr {
        width: 100%;
        margin-top: 100px;
    }
    #mainright {
        width: 100%;
    }


    /***** contents *****/
    /* top menu */
    div.topmenu {
        width: 100%;
        margin-bottom: 20px;
        background: none;
    }
    .topmenu a.current {
        background: #69bbe3 !important;
    }
    .topmenu a {
        margin: 0;
        font-size: 14px;
        background: #737373;
        color: white;
        padding: 8px 16px;
    }
    .topmenu a:hover {
        background: #888;
        color: white;
    }

    /* shadow */
    .shadow728 {
        display: none;
    }

    /* quote */
    div.randomquote {
        width: 100%;
        margin-bottom: 20px;
        padding: 0;
    }
    .bgred {
        background: #fa3737;
        color: white;
    }

    .quotetitle {
        display: none;
    }

    .randomquote .quotecontent {
        padding-top: 14px;
    }

    /* diskspace div */
    #diskspace{
        width: 100%;
    }

    /* archive */
    .archive {
        text-align: center;
        background: none;
        background: rgba(0, 0, 0, 0.08);
        /* border-radius: 0 0 4px 4px; */
    }
    .archive div div {
        height: 125px !important;
        padding: 10px !important;
        margin: 0 1.5px !important;
        border: 1px solid #888;
    }
    .archive div div div {
        height: 16px !important;
        padding: 0 !important;
        border: none;
    }

    .archive a {
        color: black;
        text-decoration: none;
    }
    .archive a:hover {
        background-color: rgba(0, 0, 0, .05);
    }
    .archive div div div:first-child a:hover {
        background: none;
    }

    /* color info */
    .legend {
        font-size: 12px;
        font-family: 'Segoe UI', 'Roboto', arial, sans-serif;
    }
    .legend span:not(:first-child) {
        padding: 2px;
    }

    .brdtb {
        border: 1px solid rgba(0, 0, 0, 0.2);
    }

    .blue {
        background-color: #ffd72a;
        color: black;
    }

    .orange {
        color: white;
    }
    .red {
        color: white;
    }


    /* main graph */
    .box {
        width: 100%;
        background: #c4c4c4;
        zoom: 1.31;
    }
    .box8 {
        height: 720px;
    }

    .box div {
        height: 10px !important;
        margin-left: 10px;
    }
    .box div span {
        height: 10px;
    }

    /* bars */
    .p0a {
        top: 25px;
    }
    .p0b {
        top: 37px;
    }
    .ltt {
        top: 56px;
        background: none;
    }
    .ltt2 {
        top: 66px;
        background: none;
    }
    /* registrations */
    .lbl1 {
        top: 85px;
    }
    .p1a {
        top: 110px;
    }
    .p1b {
        top: 122px;
    }
    .p1c {
        top: 134px;
    }
    /* ricky */
    .lbl2 {
        top: 174px;
    }
    .p2a {
        top: 198px;
    }
    .p2b {
        top: 210px;
    }
    .p2c {
        top: 222px;
    }
    .lbl3 {
        top: 240px;
    }
    .p3a {
        top: 264px;
    }
    .p3b {
        top: 276px;
    }
    .p3c {
        top: 288px;
    }
    /* johnny */
    .lbl4 {
        top: 328px;
    }
    .p4a {
        top: 352px;
    }
    .p4b {
        top: 364px;
    }
    .p4c {
        top: 376px;
    }
    .lbl5 {
        top: 394px;
    }
    .p5a {
        top: 418px;
    }
    .p5b {
        top: 429.8px;
    }
    .p5c {
        top: 442px;
    }
    /* tommy */
    .lbl6 {
        top: 482px;
    }
    .p6a {
        top: 506px;
    }
    .p6b {
        top: 517.8px;
    }
    .p6c {
        top: 530px;
    }
    .lbl7 {
        top: 548px;
    }
    .p7a {
        top: 572px;
    }
    .p7b {
        top: 583.8px;
    }
    .p7c {
        top: 596px;
    }
    /* nameservers */
    .lbl8 {
        top: 636px;
    }
    .p8a {
        top: 660px;
    }
    .p8b {
        top: 672px;
    }

    .lbl0, .lbl1, .lbl2, .lbl3, .lbl4, .lbl5, .lbl6, .lbl7, .lbl8 {
        padding: 0;
        left: 1px;
    }

    .box div.nh {
        height: 14px !important;
    }


    /* note */
    .content p.small:nth-child(2) {
        background-color: rgba(0, 0, 0, .08) !important;
        padding: 10px;
        /* font-style: italic; */
        font-size: 13px;
        font-family: 'Segoe UI', 'Roboto', arial, sans-serif;
    }


    /* questions */
    .content p:nth-child(3) {
        color: #a830c0 !important;
        font-size: 22px;
        line-height: 20px;
    }
    .content p:nth-child(4) span {
        color: #a830c0 !important;
        font-size: 14px;
        font-weight: bold;
    }
    .content p:nth-child(4) {
        font-size: 13px;
    }


    /* changelog */
    .changelog p:nth-child(1) {
        display: none;
    }
    .changelog p:nth-child(2) {
        display: none;
    }

    .changelog p:nth-child(5) {
        margin-top: 50px;
        font-style: italic;
        font-size: 10px;
        color: gray;
    }
    .changelog p:nth-child(5) b {
        font-weight: normal !important;
    }
    .changelog p:nth-child(4)::after {
        display: block;
        content: '2020-02-24 - New and modern styling added by Kristoffer.';
    }



    /***** footer *****/
    #footer {
        width: 100%;
        background: #444;
        color: white;
        height: 64px;
    }
    #footer p {
        font-size: 12px;
    }




    /***** disk space *****/
    .dsgray {
        background: #d7d7d7;
    }
    /* .dsgray div, .dsred div, .dsorange div {
        background: linear-gradient(90deg, rgba(0,36,7,0) 70%, rgba(23,255,0,1) 100%) !important;
    } */
    .dsgray div div {
        background: initial !important;
    }
    .dsred {
        background: #ff4545;
    }
    .dsorange {
        background: #ffb732;
    }



    /***** server time *****/
    .bgyellow {
        /* color: white; */
        background: #9ec1ff;
    }
}
