css:
@-moz-document domain("search.lilo.org") {
.addivwhite, .addivwhite2, #sidebar {
    display: none !important;
}
.bandeau {
    background: white;
    position: fixed;
    top: 0;
    width: 100%;
    border-bottom-color: hsl(0, 0%, 85%);
    z-index: 1;
}
.search-bar {
    height: 42px;
}
.searchBarButton {
    margin-bottom: 12px;
}
.ui-autocomplete {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 16px;
}
.ui-menu .ui-menu-item {
    padding-left: 0.9em;
}
.ui-state-focus, .ui-widget-content .ui-state-focus {
    border-color: #eee;
    background: #eee;
}
#tabsarea {
    height: 60px !important;
}
#tabsarea .caret {
    margin: 9px 5px 7px 12px;
}
.scrollable {
    color: #5f6368;
}
#resultcontainer, #resultcontainer.tab_image {
    margin-top: 135px;
    margin-left: 165px;
}
#resultcontainer.tab_news {
    margin-top: 145px;
}
#nbresults {
    display: inherit !important;
    padding-bottom: 8px;
    border-bottom: none;
    color: #70757a;
}
#results {
    max-width: 632px;
}
.result {
    margin-bottom: 27px;
}
.addivwhite2 + .result h2 {
    margin-top: 10px;
}
.result h2, .news h2 {
    font-weight: normal;
    color: #222;
    padding-bottom: 6px;
}
.result > p:first-child {
    color:rgb(34, 34, 34);
    font-size:20px;
    font-weight:400;
    margin: 6px 0;
}
.news:first-child .news + h2 {
    margin-top: 20px;
}
.result a {
    color: #1a0dab;
}
.result a:visited {
    color: #609;
}
.result>a:first-child {
    font-size: 20px;
}
.resultthumbnail {
    margin-top: 2px;
    line-height: 1.57;
    font-size: 14px;
}
.resulturl {
    margin-top: 3px;
    font-size: 15px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
a.searchmore {
    font-size: 16px;
}
a.searchmore:before {
    content: "âžœ";
    margin-right:8px;
    font-size: 1.1em;
}
.news .infos2 {
    display: none;
}
.video {
    box-shadow: none;
    border: 1px solid #dfe1e5;
    border-radius: 8px;
    overflow: hidden;
}
}
