css:
@-moz-document domain("celtx.com") {
/* An CeltX re-color by u/Apache3348

-> https://www.reddit.com/user/Apache_3348
-> https://github.com/ApacheTheHelicopter

See my other styles
        --> https://userstyles.org/users/849222

My Patreon (german)
        --> https://www.patreon.com/apache


*/
/* License info: Feel free to change the style at any time and reupload it 
(note: Leave this tag here and add your own below) */
/* Colors
    Go to color.adobe.com if you want to get some preset color packs
     You can change these at any given point of time
      Invert the Accent and Background Color if you want light theme */
:root {
    --Accent: #f2ecd8;
    --BG: #262626;
    --LBG: #3b3837;
    --Highlight: #f2a35e;
    --Icon: #a65a2e;
	--BGTrans: #262626ab;
}

/* Color Section END */
/* ----------------------- Style-optimization -----------------------
 (Experimental) Gets rid of white backgrounds with no content to edit */
html {
    background-color: transparent !important;
}

body {
    background-color: transparent !important;
    color: var(--Accent) !important;
}

header {
    background-color: var(--BG)!important;
}

footer {
    background-color: var(--BG)!important;
}
/* kp was das hier machtt */
.editor-container,
.cx-pm-aveditor .editor-container,
.cx-pm-av2editor .editor-container,
.cx-pm-stageplayeditor .editor-container {
    background-color: var(--BG)!important;
}


a:hover {
	color: var(--Highlight);
	transition: color 150ms ease;
}
a {
	color: var(--Accent);
	transition: color 150ms ease;
}


/* --------------  Titelseite  ------------ */

textarea {
    color: var(--BG) !important;
    background: var(--Highlight);
}



/* --------------  Rechtschreibpr√ºfung ------------ */

.cx-control-bar .cx-control-container {
    background-color: var(--LBG);
    box-shadow: 0px -5px 10px var(--Icon) !important;
}
.input {
    color: var(--BG) !Important;
	border: 0px !important;
}
.cx-suggestion-list-container {
    background-color: var(--BG) !important;
}
.cx-suggestion-list-item  {
    background-color: var(--BG) !important;
}
select {
    background-color: var(--BG) !important;
	color: var(--Accent);
}
option {
    color: var(--Highlight) !important;
}
.cx-select-spellcheck-language {
    color: var(--Accent) !important;
}
/* --------------  Sidebar ------------ */
#sidebar {
    background-color: var(--BGTrans) !important;
    color: var(--Accent)!important;
}
/* new project btn */
.btn-primary {
    background-color: var(--Highlight) !important;
	color: var(--BG) !important;
	transition: background-color 250ms ease !important;
}

.btn-primary:hover {
    background-color: var(--BG) !important;
	color: var(--Accent) !important;
	transition: background-color 250ms ease !important;
}
.nav-pills > .active > a {
	background-color: var(--BGTrans);
}

.cx-nav-submenu-item {
	color: var(--Icon);
}
.cx-nav-submenu-item:hover {
	color: var(--Accent);
}
a:visited {
	color: var(--Highlight);
}

/* --------------  Header ------------ */

#toolbar {
	background: var(--BGTrans);
	color: var(--Accent) !important;
}


.notification-btn-dot {
    background: var(--Highlight) !important;
}
#notifications-btn {
    color: var(--Icon) !important;
}

.navbar-inner,
.cx-foldertoolbar {
    background-color: var(--BG) !important;
    color: var(--Accent)!important;
}
.folderlink,
.cx-usersettingdropdown-username {
    color: var(--Accent) !important;
}

/* fix buttons */

#top-nav a.active {
	background: var(--Icon);
	color: var(--Accent) !important;
	border-bottom: 2px solid var(--Highlight) !important;
	transition: background 250ms ease !important;
	transition: color 250ms ease !important;
}
#top-nav a:hover {
	background-color: var(--Icon);
	color: var(--Accent) !important;
	transition: background-color 250ms ease !important;
	transition: color 250ms ease !important;
}
#top-nav a {
	background-color: var(--BG);
	color: var(--Highlight) !important;
	transition: background-color 250ms ease !important;
	transition: color 250ms ease !important;
}
/* --------------  Projects ------------ */
.cx-projectlistitem {
    background-color: var(--BGTrans);
    color: var(--Accent);
	border-bottom: 2px solid var(--Icon) !important;
	transition: border-bottom 250ms ease !important;
	transition: background-color 250ms ease !important;
}
.cx-projectlistitem:hover {
    background-color: var(--LBG);
    color: var(--Accent);
	border-bottom: 2px solid var(--Highlight) !important;
	transition: border-bottom 250ms ease !important;
	transition: background-color 250ms ease !important;
}
/* text */
.projecttitle-link,
.sharing-team-label {
    color: var(--Accent);
}
span {
    color: var(--Accent) !important;
}

/* share btn */
.unifiedsharing-button {
    background-color: var(--Highlight) !important;
    color: var(--BG)!important;
}
.unifiedsharing-button:hover {
    background-color: var(--BG) !important;
    color: var(--Accent)!important;
}

/* --------------  Lade screens  ------------ */
.modal-dialog {
    background-color: var(--BG) !important;
    background: var(--BG) !important;
}
.cx-processinglabel {
    background: var(--BG) !important;
    color: var(--Accent) !important;
}
.cx-pm-editor .ProseMirror-wrapper {
    background-color: var(--BG) !important;
    background: var(--BG) !important;
}

/* -----------------  Einstellungen ---------------- */

.infomanagerbox {
	background-color: var(--BGTrans);
}
.infomanagerbox-label {
	color: var(--Accent);
}
label.togglebox {
	background: var(--BG) !important;
	border: 0px solid;
}
label.togglebox::before {
	color: var(--LBG) !important;
	text-shadow: none !important;
}
label.togglebox input[type="checkbox"] ~ .toggle::before {
	color: var(--Accent) !important;
	background: var(--Icon);
}
.studio-management-plan-container .infomanagerbox-title {
	background: var(--Icon);
	border: none !important;
	color: var(--BGTrans);
}
/* --------------  Script Auswahl menu  ------------ */
#filelistscroll-container {
    color: var(--Accent);
}

/* Profile */
.unifiedsharing-person-circle {
	border: 0px solid !important;
	margin-top: 9px !important;
}


/*Ad remove*/
.trial-bar,
.trial-bar-holiday,
.trial-ended,
.btn-subscribe {
    display: none !important;
}

/* BG */
#projectlist-container,
#scroll-container {
        background-image: linear-gradient(rgba(12, 12, 12, .5), rgba(8, 8, 8, .5)),  url("https://i.imgur.com/GWhY2bp.png") !important;
    color: var(--Accent);
	background-repeat: no-repeat;
	background-position: center center;
	background-size: cover;
}

.studiohead-container {
	border-radius: 10px;
	background: var(--BGTrans);
}
.navbar  .brand {
	border-radius: 10px;
	background: var(--BGTrans) !important;
}


.lastmod-name {
	color: var(--Accent) !important;
}
.lastmod-date {
	color: var(--Accent) !important;
}


/* Menu Text */

/* remove white hidden BG */
.accordion-container {
	background: none !important;
}

.accordion-container header {
	border-top: none !important;
}
 .image-header {
	border-top: none !important;
}
.other-files-header{
	border-top: none !important;
}
.project-sidebar {
	border-radius: 10px !important;
	background: var(--BG);
}
.accordion-title,
.other-files-title {
    color: var(--Accent) !important;
}
.image-header,
.image-library.locked {
    background-color: var(--BGTrans) !important;
    color: var(--BG) !important;
}
#content {
    background-color: var(--BGTrans) !important;
    color: var(--BG) !important;
}
.doc-block {
    background-color: var(--BGTrans) !important;
    color: var(--Accent) !important;
	transition: background-color 250ms ease !important;
}
.doc-block:hover {
    background-color: var(--Icon) !important;
    color: var(--Accent) !important;
	transition: background-color 250ms ease !important;
}
.doc-block-title,
.integrated-doc-block,
.doc-block-readonly,
.doc-block-locked {
    color: var(--Accent) !important;
}

.integrated-doc-block .doc-block-title a {
    color: var(--Accent) !important;
}
/* --------------  Script Editor  ------------ */
/* editor bg */
.cx-pm-editor .ProseMirror-menubar-wrapper,
.cx-pm-editor .ProseMirror-wrapper,
.cx-pm-editor .cx-screenplay.cxscript,
body{
    background-image: linear-gradient(rgba(12, 12, 12, .5), rgba(8, 8, 8, .5)),  url("https://i.imgur.com/GWhY2bp.png") !important;
    color: var(--Accent);
	background-repeat: no-repeat;
	background-position: center center;
	background-size: cover;
}

/* Page */
.cx-pm-editor .cx-screenplay div.page {
    background-color: var(--BGTrans)!important;
    color: var(--BG);
    box-shadow: 0px 3px 5px var(--BGTrans) !important;
    border: none;

}


/* Formatierungen */
.sceneheading {
    background-color: var(--Highlight) !important;
}
.cxsceneheading-content {
    color: var(--BG) !important;
}
.action {
    color: var(--Accent) !important;
}
.dialog {
    color: var(--Accent) !important;
}
.character {
    color: #70CFFF !important;
}
.parenthetical {
    color: #B0AB9D !important;
}
.shot {
    color: #70FF99 !important;
}
.transition {
    color: #FF8A8A !important;
}
.text {
    color: #FFCB8A !important;
}
.act {
    color: #FF9B70 !important;
}

/* Page number */
.cx-pm-editor .cx-screenplay div.page .cxpage-meta-content .cxpage-meta-line-1 .cxpage-meta-line-1-col-3 {
    color: var(--LBG) !important;
}

/* --------------  Navigation  ------------ */

/* Header im Editor */
#toolbar-container * {
	background-color: transparent !important;
}
.file-header-content-bg {
	    background-image: linear-gradient(rgba(12, 12, 12, .5), rgba(8, 8, 8, .5)),  url("https://i.imgur.com/GWhY2bp.png") !important;
    color: var(--Accent);
	background-repeat: no-repeat;
	background-position: 0px 0px;
	background-size: 150%;
	opacity: .6;
	transition: opacity 250ms ease !important;
}
.file-header-content-bg:hover {
	    background-image: linear-gradient(rgba(12, 12, 12, .5), rgba(8, 8, 8, .5)),  url("https://i.imgur.com/GWhY2bp.png") !important;
    color: var(--Accent);
	background-repeat: no-repeat;
	background-position: 0px 0px;
	background-size: 150%;
	opacity: 1;
	transition: opacity 250ms ease !important;
}
#toolbar-container * >.file-menu-dropdown-item {
	background-color: var(--LBG) !important;
	border: 1px solid var(--LBG) !important;
	transition: background-color 250ms ease !important;
	transition: border 250ms ease !important;
}
#toolbar-container * >.file-menu-dropdown-item:hover  {
	background-color: var(--Icon) !important;
	border: 1px solid var(--Icon) !important;
	transition: background-color 250ms ease !important;
	transition: border 250ms ease !important;
}
/* Project name itle */
.cx-projectpalette-title-open.cx-projectpalette-title-main {
    background-color: var(--Icon);
}


.cx-projectpalette-title {
    color: var(--Accent) !important;
    background-color: var(--Icon);
}
/* Szenen */
.scenenav-list-item {
    color: var(--Accent) !important;
	border: none !important;
	border-radius: 0px 0px 5px 0px!important;
	transition: background-image 250ms ease !important;
    background-image: linear-gradient(rgba(12, 12, 12, .5), rgba(8, 8, 8, .5)),  url("https://i.imgur.com/GWhY2bp.png");
}
.scenenav-list-item .scenenav-list-link {
    color: var(--Highlight);
}
.scenenav-list-item.highlight {
    color: var(--BG) !important;
	transition: background-image 250ms ease !important;
	border: none !important;
    background-image: linear-gradient(rgba(12, 12, 12, .2), rgba(12, 12, 12, .2)),  url("https://i.imgur.com/GWhY2bp.png");
}
.scenenav-list-item-move-hover {
	color: var(--BG) !important;
	transition: background-image 250ms ease !important;
	border: none !important;
    background-image: linear-gradient(rgba(12, 12, 12, .2), rgba(12, 12, 12, .2)),  url("https://i.imgur.com/GWhY2bp.png");
}
/* BG rework */
.cx-projectpalette-menu-item{
	background-image: linear-gradient(rgba(12, 12, 12, .5), rgba(8, 8, 8, .5)),  url("https://i.imgur.com/GWhY2bp.png") 
}
/* --------------  Statistics  ------------ */
.editor-status-bar {
    color: var(--Accent) !important;
}
#statusBarDiv {
    background-image: linear-gradient(rgba(12, 12, 12, .5), rgba(8, 8, 8, .5)),  url("https://i.imgur.com/GWhY2bp.png");
	background-color: rgb(0, 0, 0) !important;
	
}
}
