}

/* Entry */
.c-compact-river__entry {
    border-image: linear-gradient(to right, #e16b37 , #2e5027);
    border-image-slice: 1;
}

/* groups */
.c-group-header__desc {
    color: #e16b36;
}

/* articles */
.c-group-header {
    border-image: linear-gradient(to right, #e16b37 , #2e5027);
    border-image-slice: 1;
}
span.e-image__image {
    border-image: linear-gradient(to right, #e16b37 , #2e5027);
    border-image-slice: 1;
}

/* archives */
.p-page-title {
    border-image: linear-gradient(to right, #e16b37 , #2e5027);
