css:
@-moz-document domain("boop.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #8224e3 #1c1c1c;
}
*{
    box-shadow: none;
}
input{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #8224e3;
}
/*HEADER*/
.g1-hb-row-b .g1-row-background{
    background: #1c1c1c;
}
.g1-hb-row-b .menu-item > a{
    color: #fff;
}
.g1-sticky-top-wrapper{
    border-bottom: 1px solid #8224e3;
}
.g1-dropable .g1-drop-content{
    background: #1c1c1c;
    border: 1px solid #8224e3;
}
.g1-hb-row-a .g1-row-background{
    border: 0;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.g1-row-layout-page > .g1-row-background{
    background: #1c1c1c;
}
.g1-delta,.entry-title > a{
    color: #8224e3;
}
.entry-tpl-grid:hover .entry-title > a,.entry-tpl-list-s:hover .entry-title > a,.entry-tpl-listxxs:hover .entry-title > a,.entry-date{
    color: #fff;
}
.entry-category,.g1-collection-more .g1-button{
    background: transparent;
    border: 1px solid #8224e3;
    color: #8224e3;
}
.entry-categories .entry-category:hover,.g1-button-solid, .g1-button-solid:hover{
    background: #8224e3;
    color: #1c1c1c;
    border: 1px solid #8224e3;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.g1-mega,.g1-beta, h2,.entry-tag,blockquote,.g1-gamma, h3{
    color: #8224e3;
}
.g1-button-s:hover{
    background: #8224e3;
    color: #1c1c1c;
    border: 1px solid #8224e3;
}
.g1-button-l{
    background: transparent;
    border: 1px solid #8224e3;
    color: #8224e3;
}
.change-detail-title{
    color: #fff;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
.g1-filter-pill{
    background: transparent;
    border: 1px solid #8224e3;
    color: #8224e3;
}
.g1-filter-pill:hover{
    background: #8224e3;
    color: #1c1c1c;
}
.page-title,.g1-link-xl,.g1-pagination-item .g1-link:hover,.g1-zeta, h6{
    color: #8224e3 !important;
}
#sklepaktualizacja{
    background: #8224e3;
}
.g1-pagination-item .g1-link{
    color: #fff;
}
/*PODSTRONY*/
/*FOOTER*/
.g1-footer-text{
    color: #fff;
}
.g1-footer-text:after{
    content: " | Ciemny motyw by Maciejozaman";
    color: #8224e3;
    cursor: none;
}
.g1-footer a{
    color: #8224e3;
}
.g1-footer .current-menu-item > a{
    color: #ff0036
}
/*FOOTER*/
}
