css:
@-moz-document domain(gitscrum.com) {
  body, .md-fab.md-fab-accent, .uk-modal-dialog{
    background-color: #1f1f1f;
    font-family: 'Fira Code', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen-Sans, Ubuntu, Cantarell, "Helvetica Neue", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol" !important;
}

.header_full #sidebar_main .sidebar_min, #sidebar_main, .uk-column-kanban, #header_main .uk-navbar .uk-navbar-nav>li .uk-dropdown, .typeahead, .md-card{
    background-color: #2b2b2b;
    border: none;
}

.scrum_task{
    background-color: #333333;
}

.uk-text-bold, .uk-column-kanban header span, .inline-text-static.uk-text-break, .md-card .md-card-toolbar-heading-text{
    color: white !important;
    font-weight: 500;
}

.material-icons, i.material-icons.md-10, .timer i.material-icons[data-v-b696cc9e], .md-bg-blue-grey-100, .uk-modal-dialog *{
    color: white !important;
}

.add-new-task, .add-task-container, .add-task-container[data-v-162856e5], .md-bg-blue-grey-100, .uk-tab>li.uk-active>a{
    background: #2b2b2b !important;
    border-top: #2b2b2b !important;
    color: #fff !important;
    font-weight: 300 !important;
}

.uk-badge-outline, .timer, .timer[data-v-b696cc9e], #project-head .uk-badge{
    background: rgb(78, 83, 90);
    color: #ffffff !important;
}

#header_main{
    background: #1f1f1f;
}

#sidebar_main a {
    color: #797979 !important;
}

::-webkit-scrollbar{
    height: 8px !important;
}

.uk-tab>li.uk-active[data-v-07063fd8], .uk-modal-vote[data-v-07063fd8], .uk-modal-dates[data-v-58fa9ec0]{
    background: #353535;
}

.issue-center {
    background: rgb(36, 36, 36);
    border-color: rgb(36, 36, 36);
}

hr{
    border-top: 1px solid #383838;
}

select option, textarea.md-input[data-v-a8fc8cae], textarea.md-input[data-v-a8fc8cae]:focus, .autocomplete__box, .autocomplete__inputs input, .uk-margin-small-top.uk-margin-small-bottom, .md-card, .search-input[data-v-45b6da02], .search-input input[data-v-45b6da02] {
    background: #242424 !important;
    color: #fff;
}

.uk-form-controls-border[data-v-07063fd8], .autocomplete__box, .header_full #sidebar_main .uk-nav-divider{
    border: 1px solid #464646;
}

.autocomplete__results, .gdrive-btn, .gdrive-btn:hover, .gdrive-btn:link, .dropbox-dropin-btn, .dropbox-dropin-btn:link, .dropbox-dropin-btn:hover {
    background: #2d2c2c;
    border: 1px solid #2d2c2c;
}

.uk-modal-dialog .timer {
    border: none !important;
    background: #242424 !important;
}

.uk-modal-highlight, .uk-modal-subtasks, .uk-modal-subtasks[data-v-63ccb055], .uk-panel.uk-panel-box.uk-panel-box-primary.uk-margin-small{
    background: #353535 !important;
}

#style_switcher_toggle, div#drift-widget-container{
    display: none;
}

progress[value]::-webkit-progress-value, #sidebar_main .menu_section>ul>li>a .uk-badge{
    background: #4986e7 !important;
}

.md-card .md-card-toolbar{
    background-color: #212121;
}
}
