css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .card--grit
  {
    background-image: url("https://wallpapersite.com/images/wallpapers/red-2560x1600-curves-hd-10107.jpg");
    background: fixed;
  }

  html
  {
    background: fixed url("https://cdn.discordapp.com/attachments/683315918532640773/692441398539976704/1472869.jpg") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 120%;
  }

  .bg--gradient
  {
    background: fixed;
  }

  .card--a
  {
    background-image: url("https://image.freepik.com/free-photo/black-green-background-3d-rendering_33715-310.jpg");
    color: #fefeff;
    background-size: 130%;
  }

  .btn--primary
  {
    background: #0c0c0c;
    color: #ffffff;
  }

  .theme--pDefault .profile-title
  {
    color: #FFFFFF;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-levelNum
  {
    color: #FFFFFF;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-body
  {
    color: #FFFFFF;
  }
}

@-moz-document domain(nitrotype.com)
{


  .dash-copyContainer
  {
    background: #1d1e23;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #32CD32;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #1976c6;
    color: white;
  }

  .bg--gradient
  {
    background: fixed;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://cdn10.bigcommerce.com/s-raqyrv37/products/94/images/421/lime_colored_sand__37341.1454364400.1280.850.jpg?c=2") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
  }

  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ffffff;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn10.bigcommerce.com/s-raqyrv37/products/94/images/421/lime_colored_sand__37341.1454364400.1280.850.jpg?c=2") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://cdn10.bigcommerce.com/s-raqyrv37/products/94/images/421/lime_colored_sand__37341.1454364400.1280.850.jpg?c=2") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }

  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }
}
