css:
@-moz-document url-prefix("https://www.brick-hill.com/") {
:root {
/*[[Qi40P]]*/
}

body {
    background: var(--WebsiteBackgroundColour) /*[[LS93D]]*/ repeat;
    -webkit-animation: /*[[NV9EK]]*/ 225s linear infinite;
    -moz-animation: /*[[NV9EK]]*/ 225s linear infinite;
    -o-animation: /*[[NV9EK]]*/ 225s linear infinite;
    animation: /*[[NV9EK]]*/ 225s linear infinite;
    color: var(--WebsiteBodyTextColour);
    font-family: /*[[33K00]]*/ !important;
}

.admin-forum-options button,
[class*='button'],
[class*='purchase'],
[class*='blue smaller-text'],
.orange,
.green,
[class*='no-click'] {
    font-family: /*[[33K00]]*/ !important;
}

.thread-label.blue,
.thread-label.green,
.thread-label.orange,
.thread-label.red,
.thread-label {
    background: var(--ForumThreadLabelColour) !important;
    color: var(--ForumThreadLabelTextColour) !important;
    border: solid 1.2px var(--ForumThreadLabelBorderColour);
    position: relative;
    bottom: 1px;
}

[class*='col-7-12 topic'] > .thread-label.red {
    color: var(--ForumThreadDeletedLabelTextColour) !important;
    border: solid 1.31px var(--ForumThreadDeletedLabelBorderColour);
}

nav div.primary {
    background: var(--NavBarPrimaryBackgroundColour) !important;
}

.card .content,
.tabs .tab-body {
    background-color: var(--ContentCardBackgroundColour) !important;
    border-color: transparent;
    color: var(--ContentCardTextColour) !important;
    transition: background .2s;
}

.tabs .tab.active {
    background-color: var(--ContentCardBackgroundColour) !important;
    border: transparent;
    border-bottom: 1px solid var(--ContentCardBorderBottomColour);
    transition: background .2s;
}

.tabs .tab {
    color: var(--TabTextColour);
    background-color: var(--TabBackgroundColour);
    border-color: transparent;
    transition: background .2s;
}

.tabs .tab:hover {
    background-color: var(--TabBackgroundHoverColour);
}

nav div.secondary,
footer,
footer a,
.trade-picker {
    background-color: var(--NavBarSecondaryBackgroundColour);
}

.trade-picker .trade.selected {
    background: var(--TradePickerSelectedBackgroundColour) !important;
}

.trade-picker .trade:hover {
    background: var(--TradePickerSelectedBackgroundHoverColour)
}

nav div.secondary {
    color: var(--NavBarSecondaryTextColour);
    border-bottom: solid;
    border-bottom-width: 1.2px;
}

nav .info {
    border: solid;
    border-width: 1.2px;
    border-radius: 2px;
    top: -1.600px;
    position: relative;
    background-color: var(--NavBarInfoBackgroundColour);
}

.dropdown-content li:only-child *,
.dropdown-content li *,
.dropdown-content li:not(:first-child):not(:last-child) * {
    border-radius: 4px;
    background-color: var(--DropDownMenuBackgroundColour);
    color: var(--DropDownMenuTextColour);
}

.dropdown-content li *:hover {
    background-color: var(--DropDownMenuBackgroundHoverColour) !important;
}

.dropdown-content.logout-dropdown .dropdown-arrow,
.dropdown-content .dropdown-arrow {
    border-bottom: 7px solid var(--DropDownMenuBackgroundColour) !important;
}

.card .top:not(.absolute).blue,
