}

input,
textarea,
input.rigid,
textarea.rigid {
    background: var(--TextBoxBackgroundColour);
    border: 1.2px solid var(--TextBoxBorderColour);
    color: var(--TextBoxTextColour);
    border-radius: 3px;
    resize: vertical;
    font-family: /*[[33K00]]*/ !important;
}

.input-group.fill {
    background: var(--TextBoxBackgroundColour);
    border: 1.2px solid var(--TextBoxBorderColour);
    color: var(--TextBoxTextColour);
    border-radius: 3px;
}

