css:
@-moz-document domain("webniusy.com") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #fff #1c1c1c;
}
*{
    box-shadow: none !important;
}
[type="button"], [type="reset"], [type="submit"], button{
    padding: 0px 23px;
    margin-right: 0;
    font: 700 14px/18px Poppins,Roboto,Arial,sans-serif;
}
/*HEADER*/
.g1-hb-row-c .g1-row-background{
    background: #1c1c1c;
}
.g1-hb-row-c .menu-item > a,.g1-drop-the-search .g1-drop-toggle-icon::before{
    color: #fff;
}
.g1-hb-row-c .menu-item > a:hover{
    text-decoration: underline;
}
.g1-dropable .g1-drop-content{
    top: 120%;
    width: 260px;
    height: 50px;
    margin-right: 45px;
}
.search-field{
    height: 30px;
}
.g1-dropable .g1-drop-the-search .search-form{
    margin: 10px 10px;
}
.search-form .search-submit{
    background: #000;
    border: 1px solid #fff;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.g1-row{
    background: #1c1c1c;
}
.g1-delta, h4,.g1-gamma, h3,.entry-byline .entry-date{
    color: #fff;
}
.g1-button{
    background: transparent;
    border-color: #fff;
}
.g1-button:hover{
    background: #fff;
    color: #1c1c1c;
}
.g1-collection-more-inner::before,.g1-collection-more-inner::after,.g1-sidebar .widget > header .widgettitle::before,.g1-sidebar .widget > header .widgettitle::after{
    opacity: 1;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.g1-mega,.entry-meta-m .entry-date,h5{
    color: #fff;
}
.g1-sharebar > .g1-row-background{
    background: #1c1c1c;
    border-bottom: 1px solid #fff;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
.g1-row-layout-page > .g1-row-background{
    background: #1c1c1c;
}
/*PODSTRONY*/
/*FOOTER*/
.g1-footer > .g1-row-background, .g1-footer .g1-current-background{
    background: #1c1c1c;
}
.g1-footer-text{
    color: #fff;
    width: 51%;
}
.g1-footer-text:before{
    content: "Ciemny motyw by Maciejozman ";
    color: #ff1b1b;
}
.g1-footer-nav{
    color: #fff;
    width: 49%;
}
.g1-footer .current-menu-item > a{
    color: #ff1b1b;
}
/*FOOTER*/
}
