css:
@-moz-document domain("colorhunt.co") {
/* 
Created by SayRus in 2020 with love
All rights reserved. 
https://vk.com/sayrusyt 
*/ 



/*Main*/

body, #container {
    background-color: #16202a;
}

a {
    color: #f0f0f0;
}

a:hover {
    color: #b7bedb;
}

p {
    color: #d0d0d0;
}

h1 {
    color: #f1f1f1;
}

/* 
Created by SayRus in 2020 with love
All rights reserved. 
https://vk.com/sayrusyt 
*/ 

/*Page*/

#header {
    background-color: #0e1621;
}

.block {
    background-color: #232f3d;
}

#breadcrumbs #sort {
    color: #f0f0f0;
}

.button {
    background-color: #16202a;
    border: 1px solid #16202a;
}

.button:hover {
    background-color: #121b24;
    border: 1px solid #121b24;
    color: #ccc;
}

.like {
    color: #d0d0d0;
}

.like:hover {
    color: #e8e7e7;
}

.dropdown {
    border-top: 1px solid #425262;
}

.dropdown .seperator {
    background-color: #425262;
}

.dropdown a {
    border: 1px solid #16202a;
}

.shadow {
    box-shadow: 0 1px 4px rgba(0, 0, 0, .51);
    transition: box-shadow .3s;
}

#extension .button {
    background-color: #232f3d;
}

#extension .button:hover {
    background-color: #2f3d4f;
    border: 1px solid #2f3d4f;
    
}

.title {
    color: #d0d0d0;
}

#credit:after {
    content: "& SayRus";
    color: #899bb4;
}

.sp-container {
    background-color: #2f3d4f;
    border: 1px solid #2f3d4f;
}

.sp-picker-container {
    width: 172px;
    border-left: solid 1px #2f3d4f;
}


/* 
Created by SayRus in 2020 with love
All rights reserved. 
https://vk.com/sayrusyt 
*/ 

/*Input*/

input[type=text] {
    color: #f0f0f0;
}

.sp-input.sp-validation-error {
    border: 1px solid #f93131;
    background: #3d2323;
}

input:hover, input:focus {
    border-color: #232f3d;
    color: #f0f0f0;
}
input[type=text] {
    line-height: 36px;
    padding: 0 12px;
    border-radius: 4px;
    transition: all .1s;
    box-sizing: border-box;
    background-color: #232f3d;
    border: 1px solid #232f3d;
    width: 100%;
    font-size: 14px;
    font-family: rubik;
    outline: none;
}

/* 
Created by SayRus in 2020 with love
All rights reserved. 
https://vk.com/sayrusyt 
*/ 

/*Scroll*/

::-webkit-scrollbar-thumb { 
height: 150px; 
background: #232f3d; 
border-radius: 4px;
} 
::-webkit-scrollbar-thumb:hover { 
background: #334457; 
border-radius: 4px;
} 
::-webkit-scrollbar { 
width: 10px; 
background: #16202a; 
} 


/* 
Created by SayRus in 2020 with love
All rights reserved. 
https://vk.com/sayrusyt 
*/
}
