css:
/* i really want this to be global */
/* Belltreeforums */
/* belltreeforums.com */

/* Main Back */
html {
	background: #28303d;
	
}

.body_wrapper {
            background: #3a4a66 ;
        }

.body_wrapper .breadcrumb a {
	color: white;
}

#pagetitle  h1, #pagetitle  h1 a {
	color: white;
}
#pagetitle  h1 a:hover {
    color: red;
}

.body_wrapper .lastnavbit span {
	color: white;
    font-weight: bold;
}

#pagetitle .description {
	color: white;
}

#above_threadlist #threadpagestats {
	color: white;
}


.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
	color: #131313;
	background: #f3f3f3;
	_background-image: none;
	display:block;
	width: 100%;
	float: left;
	min-height: 58px; /* 48px is the icon size */
	position:relative;
	border-bottom: 1px solid #e4e4e4;
	border-left: 1px solid #e0e0e0;
	border-right: 1px solid #f5f5f5;
}


.forumhead {
	background: #424f8a  ;
	_background-image: none;
	padding: 0;
	font:  bold 12px Arial, Tahoma,Calibri,Verdana,Geneva,sans-serif;
	color:#ffffff;
	clear:both;
	margin-top: 8px;
	float: left;
	border: 1px solid #61462C;
	width: 100%;
	-moz-border-radius-topright: 0px;
	-moz-border-radius-topleft: 0px;
	-webkit-border-top-right-radius: 0px;
	-webkit-border-top-left-radius: 0px;
	border-top-right-radius: 0px;
	border-top-left-radius: 0px;
}

.forumhead + .childforum .L2:first-child .forumrow, .forumhead + .L2 .forumrow {
	background: #ffffff	
}

#thread_inlinemod_form div ol li div.sticky {
	background: #dce3e8;
}

.threadbit .sticky{
	background: #eaeaea;
	border: 1px solid #c1c1c1;
	border-top: 0;
	width: 100%;
	float: left;
	display:block;
	clear:both;
	position: relative;
}

.threadbit .nonthread{
	background: #ffffff;
	border: 1px solid #d2d2d2;
	border-top: 0;
	width: 100%;
	float: left;
	display:block;
	clear:both;
	position: relative;
	padding: 0.5em 0 0.5em;
	text-indent: 10px;
}

.threadbit .nonsticky, .threadbit .deleted, .threadbit .discussionrow, .threadbit .ignored {
	border: 1px solid #d0d0d0;
	border-top: 0;
	width:100%;
	float: left;
	display:block;
	clear:both;
	position: relative;
	margin-top: 0;
}

.threadbit .nonsticky, .threadbit .discussionrow {
	background: #ffffff
}

.threadbit .deleted, .threadbit .ignored {
	background: #fffbe9 none repeat-x  ;
}

.threadbit .alt {
	background: #fffffd none   ;
}

.threadlisthead {
	font:   bold 12px Tahoma,Calibri,Verdana,Geneva,sans-serif;
	margin: 0;
	width: 100%;
	border: 1px solid #61462C;
	background: #2e5fb3 ;
	color: #ffffff;
	float: left;
	display:block;
	-moz-border-radius-topright: 0px;
	-moz-border-radius-topleft: 0px;
	-webkit-border-top-right-radius: 0px;
	-webkit-border-top-left-radius: 0px;
	border-top-right-radius: 0px;
	border-top-left-radius: 0px;
	-moz-box-shadow: -2px 2px 2px #555555;
	-webkit-box-shadow: -2px 2px 2px #555555;
	box-shadow: -2px 2px 2px #555555;
	position: relative;
}

.threadbit .pagination a {
	padding: 0 4px;
	-moz-border-radius: 2px;
	-webkit-border-radius: 2px;
	border-radius: 2px;
	color: #000000;
	font-size: 11px;
	display: inline;
	float: none;
}

body a {
	color:#2f3048;
	text-decoration:none;
}
body a:hover,
body a:hover .time,
body a:hover .shade,
body a:hover .understate {
	color:#294f94;
	text-decoration:underline;
}

.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
	background: #4058a5   ;
	_background-image: none;
	border: 1px solid #61462C;
	clear:both;
	display:block;
	float: left;
	width: 100%;
	color: #ffffff;
	margin: -1px -1px 0;
	font:   normal 12px Tahoma,Calibri,Verdana,Geneva,sans-serif;
	padding: 4px 0;
}

.after_content { visibility: hidden; margin: 0px;height: 0px; width: 0%;}

 .userinfo_extra dd img {visibility: hidden} 

.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols {
	padding: 6px 0 4px;
	display:block;
	background: #e2e5ea none   ;
	_background-image: none;
	font:   bold 11px Tahoma, Calibri, Verdana, Geneva, sans-serif;
	width: 100%;
	margin-right: 10px;
}

.postbitlegacy .userinfo {
	float:left;
	position: relative;
	width:180px; 
	padding: 2px 10px 5px;
	font-size: 11px;
	display:block;
        background: #f4f7ff none   ;
	clear:both;
}
