css:
@-moz-document url-prefix("https://moj.esdnevnik.rs/") {
* {
    color: white !important;
}
.top-bar .logo a .logotype::before {
content: "е-Гневник" !important;
color: white;
}
.top-bar .logo a .logotype {
    color: #141417 !important;
     text-shadow: none !important;
}
.top-bar .logo a .logotype small {
display: none;
}
.side-bar {
background-color: #141417;
width: 110px !important;
}
body, .events .event .event-timeline .event-meta {
background-color: #1e2023;
}
.courses-wrap, .stats {
    align-items: center;
}
.courses-teachers-wrap {
    display: flex;
    max-width: none;
    width: 80%;
    flex-wrap: wrap;
    justify-content: center;
}
.part-item, .items, .stats-item, .events .event .event-data .cloud, .grades-absents-wrap .courses-wrap .grades-absents, .grades-absents-wrap .stats .stats-grade-absent, .grades-absents-course-wrap .grades-absents, .grades-absents-course-wrap .grades-absents .grades-absents-content .absents-course-wrap .part-grades .part-grade, .grades-absents-course-wrap .grades-absents .grades-absents-content .schoolyear-part .part-grades .part-grade, .grades-absents-course-wrap .grades-absents .grades-absents-content .absents-course-wrap .part-grades .part-grade:nth-child(odd), .grades-absents-course-wrap .grades-absents .grades-absents-content .schoolyear-part .part-grades .part-grade:nth-child(odd), .courses-teachers-wrap .course-wrap {
background-color: #141417 !important;
width: 95% !important;
border-radius: 20px;
}
.dropdown-wrap .dropdown,.dropdown-wrap .dropdown li a {
border: none;
}
.dropdown::-webkit-scrollbar {
  display: none;
}
.dropdown {
-ms-overflow-style: none; 
scrollbar-width: none;   
}
body::-webkit-scrollbar, div[name="courses"] .dropdown::-webkit-scrollbar {
    width: 5px;
    height: 5px;
    display: block;
    background: #141417;
}
body::-webkit-scrollbar-thumb, div[name="courses"] .dropdown::-webkit-scrollbar-thumb {
    background: #fff;
    border-radius: 5px;
}
.side-bar .student.active {
background-color: #141417;
margin-left: 5px;
margin-top: 20px;
}
.side-bar .student {
background-color: #141417;
border-radius: 40px;
margin-left: 5px;
margin-top: 20px;
}
.filter-bar {
background-color: #1e2023;left: 120px;
}
.top-bar, .dropdown-wrap .dropdown,.dropdown-wrap .dropdown li a {
background-color: #141417;
border-radius: 0px 0 20px 20px;
}
.grades-absents-course-wrap .grades-absents .grades-absents-content .absents-course-wrap:before, .grades-absents-course-wrap .grades-absents .grades-absents-content .absents-course-wrap:after, .grades-absents-course-wrap .grades-absents .grades-absents-content .schoolyear-part:after,.courses-teachers-wrap .course-wrap .teachers:before, .courses-teachers-wrap .course-wrap .teachers:after, .courses-teachers-wrap .course-wrap .teachers .teacher:after, .courses-teachers-wrap .course-wrap .teachers .teacher:before, .grades-absents-course-wrap .grades-absents .grades-absents-content .absents-course-wrap:before, .grades-absents-course-wrap .grades-absents .grades-absents-content .schoolyear-part:before {
background: white;
}
.side-bar .student.active:after {
    display: none;
}
.top-bar ul.top-navigation>li>a.router-link-exact-active {
	background-color: #000000;
}
.top-bar ul.top-navigation>li:hover>a {
background-color: black;
}
/* TELEFON KANCER CSS */
.top-bar {
 border-radius: 0;   
}
.items-course-wrap .items .items-content .absents-course-wrap:after, .items-course-wrap .items .items-content .schoolyear-part:after {
 background: none;   
 border-top: 2px solid #f5f5f5; 
}
.items-course-wrap .items .items-content .absents-course-wrap:before, .items-course-wrap .items .items-content .schoolyear-part:before {
 background: none;
 border-left: 2px solid #f5f5f5;
}
div[name="courses"] .dropdown::-webkit-scrollbar {
 display: initial;
}
.side-bar .student svg {
 fill: white !important;
 background: #414149;
}
.no-data svg {
  fill: white;
}
.filter-bar>ul>li.has-dropdown:after {
 border-color: #fff transparent transparent   
}
.counter {
background: #414149 !important;
}
li>a:hover {
 background: #414149 !important;   
}
.stats-item, .items {
padding-left: 20px !important;
}
.main-loader .loader:after, .main-loader .loader:before {
 border: 5px solid #141417;
 border-top-color: white;   
 border-bottom-color: white;
}
}
