css:
@-moz-document url-prefix("https://mmda.booru") {
/* i really want this to be global */
body {
    padding: 0px;
    background-color: #eee;
}

div#header {
    height: 4rem;
    width: 100%;
    padding: 1rem 0rem 0 1rem;
    background-color: #2196f3;
    color: #eee;
    margin: 0px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, .26);
    z-index: 101;
    position: fixed
}
#header a {
    color: #fff;
}
#header strong {
    color: #eee;
}
div.sidebar {
    z-index: 2;
    height: 100%;
    width: 20rem;
    top: 5rem;
    background-color: #fff;
    padding: 1rem 0rem 0 1rem;
    position: fixed;
    overflow-wrap: break-word;
}
div.content {
    right: 0px;
    height: calc(100% - 5rem);
    top: 5rem;
    float: right;
    position: fixed;
    overflow-y: scroll;
}
div#content {
    top: 5rem;
    float: left;
    position: fixed;
}
span.thumb {
    height: 200px;
    width: 200px;
    margin: 5px;

    box-shadow: 0 2px 5px rgba(0, 0, 0, .26)
}
img {
    /* Full height */
    width: 200px;
    height: 200px;
    object-fit: cover;
    background-size: contain;
}
a.hover img {
    opacity: 0.1;
}
#image {
    width: 80%;
    height: 100%;
    object-fit: contain;
    cursor: auto;
}
/* hidden button */
a#pi {
    position: fixed;
    bottom: 2rem;
    right: 2rem;
    z-index: 102;
}
}
