css:
@-moz-document url-prefix("https://redmine.parusnik.org/") {
#sidebar, #main {
    background: #E5EEF5;
}
#top-menu {
    padding: 8px 0 0 6px;
    background: #375D81;
    box-shadow: 0px 1px 3px black;
    border-bottom: 1px solid #40698f;
}
#header {
    background: #9fb5c9;
    background-image: -webkit-linear-gradient(top, #183152, #375D81);
}
#main-menu {
    border-top: 1px solid #e9f2fb;
    border-bottom: 1px solid #8b9fb2;
    background-image: -webkit-linear-gradient(top, #ABC8E2, #a4c1dc);
}
#main-menu li a {
    border-right: 1px solid #9fb5c9;
    border-left: 1px solid #b4d3ee;
    font-size: 11px;
    padding: 8px 10px 8px 10px;
    font-weight: bold;
    background-image: -webkit-linear-gradient(top, #ABC8E2, #a4c1dc);
    text-shadow: 1px 1px 0px #628DAE;
}
#main-menu li a.selected, #main-menu li a:hover {
    background-image: -webkit-linear-gradient(top, #97b4cf, #71a3d2);
    border-right: 1px solid #658fb7;
}
h1 {
    text-shadow: 1px 1px 5px #00253e;
}
.wiki h1 {
    text-shadow: none;
}
h2 {
    font-size: 22px;
    color: #262626;
    font-style: italic;
    padding: 5px 0 0 0;
}
h3 {
    font-size: 20px;
    color: #262626;
}
#history h3 {
    font-style: italic;
    padding: 5px 0 0 0;
}
#sidebar h3 {
    margin-top: 14px;
    padding: 5px 10px;
    font-size: 17px;
    color: #fff;
    text-shadow: 1px 1px 0px #628dae;
    font-style: italic;
    width: 100%;
    background: #b8d2e7;
    border-left: 5px solid #c5ddf0;
    box-shadow: 0px 1px 1px #8b9fb2;
}
#sidebar a {
    font-size: 12px;
    line-height: 17px;
    color: #367db4;
    text-shadow: 1px 1px 0px #f4f9ff;
}
hr {
    background: #B2CBD9;
}
table.attributes {
    background-image: -webkit-linear-gradient(top, #f8fbff, #ebf5ff);
    border-radius: 4px;
    border: none;
    padding: 10px;
    box-shadow: 0px 1px 3px #8ca6c0;
}
div.issue {
    border-radius: 4px;
    background: #e9f2fb;
    border: 1px solid #d9e1e8;
}
.contextual {
    border-radius: 4px;
    background-image: -webkit-linear-gradient(top, #f8fbff, #ebf5ff);
    padding: 2px 4px;
    box-shadow: 0px 0px 3px #92b5d9;
    margin: 4px;
}
.contextual:hover {
    box-shadow: 0px 0px 1px #92b5d9;
}
.journal {
}
table.progress {
    box-shadow: 0px 0px 1px #648bd2;
    border-radius: 4px;
    border: none;
}
table.progress td.closed {
    border-radius: 4px 0 0 4px;
    background-color: #A3BF3B;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#A3BF3B), to(#bddc4c));
}
td.priority {
    font-weight: bold;
}
thead tr, thead th {
    background: #dce6f3 !important;
    text-shadow: 1px 1px 0px #f4f9ff;
    color: #313131;
    border: 1px solid #bfcad9;
}
tr.odd {
    background: #eceff9;
}
tr {
    height: 15px !important;
    vertical-align: middle !important;
    border-spacing: 5px;
}
.box {
    background-image: -webkit-linear-gradient(top, #f8fbff, #ebf5ff);
    border-radius: 4px;
    border: none;
    box-shadow: 0px 1px 2px #e5eaf2;
    padding: 10px;
    margin-bottom: 10px;
    border: 1px solid #d2d8e1;
}
.box p {
    line-height: 20px;
}
ul.toc {
    background-image: -webkit-linear-gradient(top, #f8fbff, #ebf5ff);
    border: 1px solid #e5eaf3 !important;
    font-size: 110%;
    font-style: italic;
    padding: 10px;
}
#footer {
    background-image: -webkit-linear-gradient(top, #ABC8E2, #a4c1dc);
    text-shadow: 1px 1px 0px #628DAE;
}
#header h1 {
    overflow: hidden;
    color: #e5feff;
    font-weight: normal;
    padding: 0px 0 10px 50px;
    margin: 7px;
    background-image: url(https://bwe.su/parus.png);
    background-repeat: no-repeat;
    background-size: contain;
    background-position-x: -20px;
    min-height: 60px;
}

#header h1 span {
    left: 44px;
    text-shadow: #1a3455 1px 0 0px, #1a3455 0 1px 0px, #a9c6e0 -1px 0 0px, #a9c6e0 0 -1px 0px;
}

span.separator {
    left: -7px !important;
}

#header a {
    border-bottom-color: rgba(147, 185, 108, 0.40);
    border-bottom-width: 1px;
    border-bottom-style: solid;
    color: #fff;
    text-decoration: none;
}
#main-menu li a {
    color: #fff;
    text-shadow: 0px 0px 0px #628DAE;
}
a, a:link, a:visited {
    color: #147dc0;
}
a.us_icon {
    color:#fff !important;
}

#content .tabs ul li a.selected span, body :not(#main-menu) .tabs ul li a.selected span {
    color: #147dc0;
    border-bottom-color: #147dc0;
}

#footer {
    text-shadow: 0px 0px 0px #628DAE;
}

div.journal.private-notes {
    background-color: #fbf2f6;
}

#header a {
    border-bottom-color: rgba(147, 185, 108, 0);
}

#us_fast_link_top_page_block > a > span {
    border-bottom-style: none;
}

a.no_line span, a.icon span, #main-menu li a span {
    border-bottom-color: rgba(147,185,108,0);
}

a:hover, #main-menu li a:hover{
    border-bottom-color: rgba(20, 125, 192, 0);
}

a{
    border-bottom-color: rgba(20, 125, 192, 0);
}

a, a:link, a:visited, a.green_link, a.green_link:link, a.green_link:visited, tr.issue td.subject a.green_link, tr.issue td.id a.green_link, a.issue.green_link, #main-menu li a, #main-menu li a.selected span, #content .tabs ul li a, body :not(#main-menu).tabs li a, #header a {
    border-bottom-style: none;
}

#top-menu > ul > li > a span, #dropdown_top_menu li a span {
    border-bottom-style: none;
}

#top-menu {
    height: 40px;
}

#top-menu li a > span {
    border-bottom-style: none;
}

.journal.has-notes .wiki .contextual:first-child {
    right: 5em;
}

div.gravatar-with-child > img.gravatar:nth-child(2) {
    border: 2px solid #fff;
    background-color: #375d81;
}

img.gravatar {
    background-color: #375D80;
}

body :not(#main-menu).tabs ul li a {
    color: #147dc0;
}

.journal.has-notes .wiki .contextual:first-child {
    right: 0em;
}

.journal.has-notes .wiki .contextual:first-child {
    top: 36px;
}

p {
    width: 95%;
}

#main-menu li a.new-object, #main-menu .menu-children li a {
    color: #fff;
}

.tabular p {
    padding-left: 0px;
}

div.issue {
    background-repeat: no-repeat;
    background-position-x: right;
}

a, a:link, a:visited {
    color: none;
}

a#eq-apply-query {
    color: white;
}

a.eq-button.eq-button-small.eq-button-success.eq-save {
    color: white;
}

#top-menu li a span.ac_counter, span.ac_counter {
    background-color: #102034;
}

#top-menu a span.ac_counter.unread {
    background-color: #e80e0e;
}

tr.ui-unread td.subject:before, tr.ui-updated td.subject:before {
    background-color: #e80e0e;
}

h1 {
    text-shadow: 0px 0px 0px #00253e;
}

html>body .tabular p {
    overflow: auto;
}

label {
    margin-left: 0px !important;
    padding-right: 10px;
}

input#issue_subject, input#document_title {
    max-width: 80%;
}

textarea.wiki-edit {
    max-width: 80%;
}

input.string_cf, input.link_cf {
    max-width: 55%;
}

form .attributes select {
    width: 56%;
}

.check_box_group {
    width: 80%;
    float: right;
}

textarea.text_cf {
    width: 80%;
    float: right;
}

.jstElements {
    display: block !important;
}

textarea.wiki-edit {
    left: 10.5%;
    position: relative;
}

input#time_entry_comments {
    position: relative;
    width: 85%;
}

.cf_19 div.value {
    background-color: #e1e4fb;
    padding: 5px;
}

.cf_19 .label {
    color: red;
}

#main-menu li a:hover span, a:hover.no_line span {
    border-bottom-color: inherit;
    color: #fff;
}

.select2-container--default .select2-results__option--highlighted[aria-selected] span {
    color: #da2f2f;
}

a:hover.no_line span {
    border-bottom-color: inherit;
    color: #759fcf;
}

.contextual {
    width: auto;
}

#attachments_form label {
    float: left;
    margin-bottom: 20px;
}

.attachments_fields>span, #existing-attachments>span {
    clear: both;
}

tr:hover {
    background: #c3e2ff !important;
}

.attachments table tbody tr:hover {
    background: #c3e2ff !important;
}

#top-menu > ul > li > a span, #account > ul > li > a span {
    border-bottom-color: #fff0;
}

.icon-list-action {
    filter: brightness(10);
}

#top-menu li a:hover, #top-menu .dropdown-menu > li > div.menu-item:hover, #dropdown_top_menu .dropdown-menu > li a:hover, #dropdown_top_menu .dropdown-menu > li > div.menu-item:hover, .dropdown-menu > li > div.menu-item:hover > a {
    background-color: #4d96da00;
}

.questions-filters > ul > li a.selected {
    color: #fff;
}

div.journal h4 img.gravatar {
    margin-left: 2px;
}

textarea.wiki-edit {
    left: 0%;
}

}
