css:
@-moz-document domain("bfmtv.com") {
/* Style sombre pour bfmtv.com
2020-02-28: creation
2020-04-15: minor update
2020-06-25: recréation complète
2021-05-03: update mineure: pages "live"
*/
body,
.main_wrapper {
    background: rgb(55, 55, 55);
}

.block_news {
    display: block;
    background: rgb(22, 22, 22);
    padding: 56px 5px 6px 10px;
    position: relative;
}
.block_news .list_news .content_item {
    background: rgb(55, 55, 55);
}

body {
    color: rgb(200, 200, 200);
}

.nav_primary {
    background: rgb(55, 55, 55);
    color: #000;
    border-bottom: 1px solid #e4e4e4;
}

.nav_primary li a {
    color: rgb(200, 200, 200);
}

.header_top {
    background: #022B72;
    color: rgb(200, 200, 200);
}

.main_menu_nav {
    background: rgb(55, 55, 55);
}

.menu_left li.active {
    background: rgb(55, 55, 55);
    border-top: 1px solid rgb(88, 88, 88);
    border-bottom: 1px solid rgb(88, 88, 88);
    border-right: 1px solid rgb(88, 88, 88);
}

.menu_right {
    background: rgb(55, 55, 55);
}

.menu_right {
    border: 1px solid rgb(88, 88, 88);
}

.menu_left a,
.menu_center_left a,
.menu_center_right a {
    color: rgb(200, 200, 200);
}

.menu_center_left a:hover {
    color: #0560FF;
}

.menu_center_left li {
    border-bottom: 1px solid rgb(88, 88, 88);
}

.menu_center_right {
    background-color: #022B72;
}

.menu_center_title,
.content_body_wrapper,
.content_live_block {
    color: rgb(200, 200, 200);
    background: rgb(55, 55, 55);
}

.menu_newsletter_title {
    color: #0560FF;
}

.nav_primary li.active,
.nav_primary li:hover {
    border-bottom-color: #ff9200;
}

.content_rubrique .content_title,
.content_rubrique .content_header,
.home_title,
.content_title {
    color: rgb(200, 200, 200);
}

.content_header {
    color: rgb(100, 100, 100);
}

.ordered_list li::before {
    color: #424242;
}

.fixed_title {
    background: rgba(55, 55, 55, .97);
}

.fixed_title .content_title {
    color: rgb(200, 200, 200);
}

.content_cover::after {
    background: rgb(55, 55, 55);
}

.content_body a {
    color: #ff9200;
}

.embed_quote,
.embed_seemore {
    border-left: 6px solid #ff9200;
}

#progress_bar {
    background: #ff9200;
}

.live_box {
	background: rgb(88, 88, 88);
	color: rgb(200, 200, 200);
}
}
