css:
@-moz-document domain("instagram.com") {
/* perisan font */
@font-face { font-family: persian3; src: url('https://cdn.rawgit.com/rastikerdar/shabnam-font/v5.0.0/dist/Shabnam.woff2') format('woff2'); font-weight: normal; }
* { font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif !important; }
span { font-family: persian3, Tahoma, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif !important;  }

/* main window width */
.Kj7h1, .ltEKP, .j9XKR, .PdwC2._6oveC.Z_y-9, .M9sTE.L_LMM.JyscU.ePUX4 {  max-width: 1180px !important; width: 1180px !important; background-color:#fff !important; }

/* header width */
.JyscU.ePUX4 .UE9AK {  width: 450px !important; }
span.RPhNB { display: none !important; opacity: 0; line-height:5px !important; }
header.Ppjfr.UE9AK.wdOqh *:not(.Igw0E) { text-align: right !important; display: block !important; direction: rtl !important; }
JyscU.ePUX4.L_LMM .MEAGs { right: 28px !important; }
.o-MQd.z8cbW .RqtMr .e1e1d a { margin-right: 45px !important; }
.z8cbW { margin-left: 68px !important }

/* right column width */
.JyscU .eo2As { width: 480px !important; }
ul.TCSYW .ZyFrc { width: 452px !important; }

/* logo */
.oJZym div.Igw0E.rBNOH.eGOV_.ybXk5._4EzTm { background-image: url('https://hupaa.com/images/insta.png') !important; background-repeat: no-repeat; background-size: 300px; height: 52px; }
.oJZym div.Igw0E.rBNOH.eGOV_.ybXk5._4EzTm img { display: none; }

/* search bar */
div.pbgfb.Di7vw { border-radius: 40px !important; border-color: #81c1ff !important; }
div.pbgfb.Di7vw span { color: #81c1ff !important; }
div.LWmhU._0aCwM input { border-radius: 40px !important; border-color: #81c1ff !important; }
::placeholder { color: #81c1ff !important; }

/* icons */
svg { fill:#81c1ff !important; }
svg[aria-label="Unlike"], svg[aria-label="Like"] { fill:#e30051 !important; }
svg[aria-label="Save"], svg[aria-label="Remove"] { fill:#007d4e !important; }


/* photo container width / left col */
.ZyFrc, .rQDP3, .kPFhm.B1JlO.OAXCp {max-width: 700px !important; }

/* right col bg */
.JyscU ._97aPb.wKWK0 , .JyscU ._97aPb { background-color: #fff !important; }

/* right align */
.P9YgZ,.C4VMK,.C7I1f,.X7jCj,.eo2As { direction: rtl !important; text-align: right !important;  }

/* sections */
section { direction: ltr !important; text-align: left !important;  } 

/* username in comments */
a.yWX7d._8A5w5, a.yWX7d._8A5w5, a.yWX7d._8A5w5, a, a:link, .jQgLo, a.Igw0E.rBNOH.eGOV_.ybXk5._4EzTm                                                                                                           
{ color: #239de6 !important; font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif !important; font-weight: 600 !important; font-size: 13px !important; }

/* mentions */
a.notranslate:not(.MBL3Z) { color: #a800b8 !important; }

/* time of post */
.k_Q0X.NnvRN { direction: ltr !important; text-align:left !important;  }
.k_Q0X.NnvRN a { color:#888 !important; }

/* post location in map */
a.O4GlU { color:#888 !important; font-weight:400 !important; line-height: 1.5 !important; font-family: persian3, Tahoma !important; 
font-size: 11px !important; padding-right:50px !important; }

/* username in comments */
h3._6lAjh  { display: block !important; text-align: right !important; }
.gElp9 {    word-wrap: normal !important; overflow-wrap: normal !important; }
a.sqdOP.yWX7d._8A5w5.ZIAjV { text-align: right !important; }

/* user thumb in profile page */
.XjzKX .RR-M- ._2dbep , .XjzKX ._4dMfM { width: 220px !important; height: 220px !important; border-radius: 0px !important; }
.XjzKX canvas { opacity: 0 !important; }
.XjzKX .RR-M- ._2dbep , .XjzKX ._4dMfM .M-jxE {  border-radius: 0px !important; }
.XjzKX .RR-M- ._2dbep::after , .XjzKX ._4dMfM .M-jxE::after {  border: 0px solid white; }
.XjzKX .RR-M- ._2dbep img , .XjzKX ._4dMfM .M-jxE .IalUJ img { width: 220px !important; height: 220px !important; border-radius: 0px !important; }


/* comment report menu */
._4l6NB { right: 1px !important; top: 44px !important; }

/* caption text */
div.QzzMF.Igw0E.IwRSH.eGOV_.vwCYk span { line-height:1.7 !important; font-size:18 !important; color:#3b3b91 !important; }

/* comment id & text */
.C4VMK { padding-right:5px !important; }
.C4VMK span { display: block !important; padding-right:15px !important; }

/* views */
.vcOH2 {color:#888 !important; font-weight:400 !important; }

/* comments count */
a.r8ZrO {color:#888 !important; font-weight:400 !important; }

/* hashtags */
a.xil3i { color: #00b884 !important; }

/* comment type textarea */
section.sH9wk._JgwE { direction: rtl !important; }
section.sH9wk._JgwE textarea { direction: rtl !important; font-family: persian3, Tahoma, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif !important;  }

/* comment full box */
.gElp9.rUo9f div.P9YgZ { border:solid 1px #eee; margin-bottom:3px; padding:6px; }

/* replied comments inner */
ul.TCSYW li { padding-right:80px; width:360px !important; }

/* follow button */
button.oW_lN.sqdOP.yWX7d.y3zKF, button.oW_lN.sqdOP.yWX7d._8A5w5 { color:#ff4a00 !important; margin-right: 46px !important; }

/* active save image menu */
._lz6s.aUCRo { z-index: 3; }
img.FFVAD { z-index: 2; }

/* active video controls */
article video::-webkit-media-controls-panel { opacity: 1 !important; display: -webkit-flex !important; z-index: 1000000 !important; max-height: 90px !important; margin-top: auto !important; }
article video::-webkit-media-controls-panel:hover { opacity: 1 !important; }
.PyenC { position:absolute; width:100px; z-index:10; height:100px; opacity:0.8; }
.fXIG0 { position:relative; width:100px; z-index:100; height:100px; }

/* likes count */
button.sqdOP.yWX7d._8A5w5 { color:#ff4a00 !important; }

/* story list container */
.Igw0E.IwRSH.eGOV_._4EzTm._5VUwz.ZUqME > div:first-child { height:420px !important; overflow-y: scroll !important; }

/* post owner id */
.sqdOP.yWX7d._8A5w5.ZIAjV { padding-right: 0px !important; font-size: 15px !important; } 

/* comments text */
.C4VMK span { line-height: 1.5 !important; padding-top: 8px; }

/* likes show hover thumbnails */
.qn-0x, .u7YqG { z-index: 1000;}

}
