css:
@-moz-document domain("picrew.me") {
element.style {
}
.c_btn {
    display: flex;
    justify-content: center;
    align-items: center;
    width: auto;
    padding: 8px;
    background: #9d1433;
    border-radius: 4px;
    text-align: center;
    word-break: break-all;
    color: #c8bdbd;
    cursor: pointer;
}
a {
    margin: 0;
    padding: 0;
    font-size: 100%;
    vertical-align: baseline;
    background: 0 0;
    color: inherit;
    text-decoration: none;
    box-sizing: border-box;
}
.main_footer_wrapper {
    font-size: 14px;
    background-color: #c29c1c;
}
.sitetop_imagemaker_list_wrapper {
    background: #a25e5e;
    padding: 16px;
    border-radius: 4px;
}
.sitetop_imagemaker_list a {
    display: block;
    box-sizing: border-box;
    background-color: #a25e5e;
    overflow: hidden;
}
.register_wrapper .register_content a {
    display: inline-block;
    padding: 16px 20px;
    background: #ff597e;
    border-radius: 4px;
    font-size: 20px;
    color: #000;
    font-family: "Rounded Mplus 1c";
    padding: 16px;
    font-size: 24px;
    text-align: center;
}
.ibox-content {
    background-color: #5b2a2a;
    color: inherit;
    padding: 15px 20px 20px 20px;
    border-color: #5b2a2a;
    -o-border-image: none;
    border-image: none;
    border-style: solid solid none;
    border-width: 1px 0;
}
.gray-bg {
    background-color: #5b2a2a;
}
.white-bg {
    background-color: #a44f4f;
}
.border-bottom {
    border-bottom: 1px solid #a44f4f!important;
}
.footer.fixed {
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 1000;
    padding: 10px 20px;
    background: #a44f4f;
    border-top: 1px solid #a44f4f;
    margin-left: 220px;
}
body {
    background-color: #603e59;
    background-size: 120px;
    color: #ccc5c5;
    line-height: 1.2;
    -webkit-text-size-adjust: 100%;
}
.sitetop_discovery {
    max-width: 1080px;
    margin: 8px auto;
    padding: 16px;
    background: #532f2f;
    border-radius: 4px;
}
body {
    background-color: #000;
    background-size: 120px;
    color: #d9d9d9;
    line-height: 1.2;
    -webkit-text-size-adjust: 100%;
}
.target_list li {
    display: flex;
    align-items: center;
    margin-bottom: 24px;
    padding: 8px 36px;
    background: #442c2c;
    color: #e8e4e4;
    border-radius: 8px;
}
.btn-primary {
    background-color: #1ab394;
    border-color: #1ab394;
    color: #030303;
}
.navbar-fixed-top, .navbar-static-top {
    background: #773858;
}
.target_wrapper {
    padding: 72px 8px;
    background: #b38900;
}
.sitetop_nav {
    background: #605b3e;
}
.sitetop_sidebar_content {
    width: 100%;
    margin-bottom: 8px;
    padding: 16px;
    background-color: #605b3e;
    border-radius: 4px;
}
article, aside, details, figcaption, figure, footer, header, hgroup, menu, nav, section {
    display: block;
}
abbr, address, article, aside, audio, b, blockquote, body, button, canvas, caption, cite, code, dd, del, details, dfn, div, dl, dt, em, fieldset, figcaption, figure, footer, form, h1, h2, h3, h4, h5, h6, header, hgroup, html, i, iframe, img, ins, kbd, label, legend, li, mark, menu, nav, object, ol, p, pre, q, samp, section, small, span, strong, sub, summary, sup, table, tbody, td, tfoot, th, thead, time, tr, ul, var, video {
    margin: 0;
    padding: 0;
    border: 0;
    outline: 0;
    font-size: 100%;
    font-weight: 400;
    vertical-align: baseline;
    background: 0 0;
    box-sizing: border-box;
}
* {
    font-family: 'Lucida Grande','Hiragino Kaku Gothic ProN',Meiryo,sans-serif;
    min-height: 0;
    min-width: 0;
    word-wrap: break-word;
}
