css:
@-moz-document url-prefix("https://bumble.com/") {
body {
     background-color:#333 !important;
}
.page__content, .scroll__inner, .messages-header, .sidebar__header, .sidebar__contact-list {
     background-color:#333 !important;
}
.message, .message--in, .message-bubble {
     color:#222 !important;
}
.messages-header__content, .sidebar-profile__name, .sidebar-action-banner {
     color:#ccc !important;
}
.sidebar-action-banner {
     background-color:#555 !important;
}
.contact-promo, .contact-promo--feature-boost, .contact-tabs__section-header {
     visibility:hidden !important;
}
.contact-tabs__section-content, .contact, .theme-dating {
     background-color:#333 !important;
}
.contact__name  {
     color:#eee !important;
}
.contact {
     border-top: 1px #555 solid !important;
}
.contact__message  {
     color:#ccc !important;
}


/* * {
     border:hidden !important;
}
*/
.messages-header {
     border-bottom: 1px #555 solid !important;
}
.message-field__inner {
     border: none;
     background-color: #212121;
     color: #ccc;
}
.textarea__inner, .textarea__input {
     color: #a2a2a2 !important;
}
}
