@-moz-document domain("lolzteam.online") {

  .button.primary:hover
  {
    background: #3a88a9 radial-gradient(circle, transparent 1%, #3a88a9 1%) center/15000%;
  }

  .button.primary
  {
    background-color: #226a8e;
  }

  .nodeList .node.current > .nodeInfo > .nodeText > .nodeTitle > a, .nodeList .node .current>div>.nodeTitle>a
  {
    color: #226a8e;
  }

  .node.current .nodeText .nodeTitle a::before
  {
    color: #226a8e;
  }

  .xenOverlay .section .heading, .xenOverlay .sectionMain .heading, .xenOverlay .errorOverlay .heading
  {
    background: #226a8e;
  }

  .mainc
  {
    color: #226a8e;
  }

  .breadcrumb .crust:last-child a.crumb
  {
    color: #226a8e;
  }

  .hasUnreadArticles
  {
    background: #226a8e;
  }

  #searchBar .primaryControls:before
  {
    color: #226a8e;
  }

  #allnotifications
  {
    color: #226a8e;
  }

  .listItemText a
  {
    color: #226a8e;
  }

