  .navTabs .navTab.account.PopupOpen .accountUsername
  {
    color: #226a8e;
  }

  .navTabs .navTab.account:hover .accountUsername
  {
    color: #226a8e;
  }

  .navigationSideBar a.selected
  {
    -webkit-box-shadow: inset 4px 0px 0px 0px rgba(58, 113, 169,1);
    -moz-box-shadow: inset 4px 0px 0px 0px rgba(58, 113, 169,1);
    box-shadow: inset 4px 0px 0px 0px rgba(58, 113, 169,1);
  }

  .text_Ads .discussionListItem.item .title
  {
    color: #226a8e;
  }

  span.checked
  {
    background-color: #226a8e;
  }

  .marketPrefix
  {
    background: rgb(48, 132, 184);
  }

  .prefix.shop_pref
  {
    background: #5b608e;
  }

  .conversationItem .messageStateIcon::before
  {
    content: '\f560';
    color: #226a8e;
  }

  .navTabs .navTab.PopupClosed .navLink:hover .counter-container img, .navTabs .navTab.PopupOpen .navLink .counter-container img
  {
    filter: hue-rotate(40deg);
    --webkit-filter: hue-rotate(40deg);
  }

  .page_counter .count
  {
    color: #226a8e;
  }

  .marketViewedItems .trimmedTitle a
  {
    color: #226a8e;
  }

  .marketMyPayments > .item .amountChange .in
  {
    color: #226a8e;
  }

