  }

  .balanceValue
  {
    color: #226a8e;
  }

  input[type="radio"]:before
  {
    background: #226a8e;
  }

  .tabs li.active
  {
    box-shadow: inset 0px -2px 0px 0px #226a8e;
  }

  #lzt-market-logo
  {
    filter: hue-rotate(45deg);
  }

  .marketSidebarMenu .svgIcon svg
  {
    filter: hue-rotate(45deg);
  }

  .marketSidebarMenu a.selected
  {
    box-shadow: inset 2.5px 0 0 0 #226a8e;
  }

  input.textCtrl:focus, select.textCtrl:focus
  {
    box-shadow: 0 0 0px 2px #226a8e;
  }

  .extendedGuarantee
  {
    filter: hue-rotate(45deg);
  }

  .restorePercents.green
  {
    filter: hue-rotate(45deg);
  }

  .info-separator
  {
    background: #226a8e;
  }

  .ToFavouritesButton.added .icon
  {
    filter: hue-rotate(45deg);
  }

  .universalSearchForm:before
  {
    color: #226a8e;
  }

  .likeNodes .node .counter
  {
    color: #226a8e;
  }

  .searchResult .title>a
  {
    color: #226a8e;
  }

  input[type="checkbox"]:checked:after
  {
    background-color: #226a8e;
    border-color: #226a8e;
  }

  #lzt-logo
  {
    filter: hue-rotate(45deg);
  }

  #pageDescription>a, #pageDescription>a>abbr
  {
    color: #226a8e;
  }

  .emCtrl, .messageContent a:not(.button):not(.username)
  {
    color: #226a8e;
  }

  .numberStyle
  {
    color: #226a8e;
  }

  .forumSearchThreads--Link--Icon
  {
    background: #226a8e;
  }

  .forumSearchThreads--Link:hover .forumSearchThreads--Link--Icon
  {
    background: #2184b5;
  }

  .bbCodeQuote
  {
    border-color: #226a8e;
  }

  .PageNav a.currentPage
  {
    background: #226a8e;
  }

  .marketIndexItem.sticky .marketIndexItem--Price
  {
    background: #226a8e;
  }

  .itemCount
  {
    filter: hue-rotate(45deg);
  }

  .dottesStyle:hover, .dottesStyle.PopupOpen
  {
    filter: hue-rotate(45deg);
  }

  .message .item:hover
  {
    filter: hue-rotate(45deg);
  }
  .actionButton--sendMoney
  {
    background-color: #266a8e;
  }
  .actionButton--sendMoney:hover {
    background-color: #2886b8;
  }
  .marketItemView--status.clear .statusTitle
  {
   color: #226a8e;
  }
  .marketItemView--status
  {
    background: #183442;
  }
}
